INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0d553f65-4950-43c6-aa71-cb472a860940', '2024-04-12T15:27:00.330Z', 'Aesthetic Room', 'https://www.aestheticroomai.com/', 'images/aesthetic-room.webp', 'images/aesthetic-icon.svg', '{"what":"aesthetic.room is an AI interior design tool that allows users to restyle their home, office, or any space by uploading a photo of the room and receiving a design in minutes. It offers unlimited styles and combinations to create a unique space.","who":"This product is for individuals looking to redesign their living or working spaces, real estate agents wanting to enhance their listings, and anyone interested in utilizing AI technology for interior design.","best_features":["AI-powered design generation for quick and personalized room restyling","Flexible pricing plans with affordable one-time payment options","User privacy protection with photo deletion after 48 hours"]}', 'aesthetic.room is an AI interior design tool that utilizes AI technology to restyle rooms quickly and efficiently. Users can upload a photo of their room, select a style and room type, and receive a personalized design in minutes. The tool is perfect for individuals looking to transform their spaces, real estate agents wanting to enhance their listings, and anyone interested in AI-assisted interior design.', NULL, '["AI Tools","Real Estate","Interior Design"]');
