INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9898a478-c5d9-4a00-a1f6-15548b9b8123', '2024-04-12T15:27:00.331Z', 'Affistash', 'https://affistash.com', 'images/affistash.webp', 'images/affistash-icon.webp', '{"what":"Affistash is an AI tool designed to help brands discover and recruit affiliate partners efficiently. It uses automated discovery processes to match brands with perfect partners based on relevant keywords, target audience, and niche descriptions.","who":"Affistash is for brands looking to expand their partnerships effectively by streamlining the affiliate recruitment process. It is ideal for businesses in need of a tool to help them reach more partners and manage their partnerships efficiently.","best_features":["AI automated discovery process to match brands with perfect partners","Scours the entire internet to find a wide range of partners, from traditional affiliates to influencers and complementary businesses","Provides in-depth insights on partner audiences, traffic, engagement, and more"]}', 'Affistash is an AI tool that helps brands discover and recruit affiliate partners efficiently. It matches brands with perfect partners based on relevant keywords, target audience, and niche descriptions. The platform offers in-depth insights on partner audiences, traffic, engagement, and more, along with a CRM portal for easy partnership management.', NULL, '["AI Services","Affiliate Marketing","Partnership Management"]');
