INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c0116a70-8674-478c-bbba-a7fc9b0e5824', '2024-04-12T15:27:00.460Z', 'Ask Your PDF', 'https://askyourpdf.com/', 'images/ask-your-pdf.webp', 'images/ask-your-pdf-icon.webp', '{"what":"AskYourPDF is an AI tool that allows users to interact with PDF documents in a conversational manner, using ChatGPT technology. Users can upload PDF documents and engage in conversations with the AI chatbot to receive answers and insights. The tool also offers features like tracking conversations and chatting with multiple documents at once.","who":"AskYourPDF is for individuals and professionals who work with PDF documents and want a more interactive and engaging way to interact with them. It is suitable for students, researchers, business professionals, and anyone looking to enhance their document reading experience.","best_features":["Interactive Conversations: Users can have engaging conversations with their PDF documents, making the reading experience more interactive.","Smart Navigation: AI-powered chat system provides accurate information and fosters an enjoyable learning environment.","Multiple Document Chat: Users can chat with multiple documents at once, enhancing productivity and efficiency."],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"","features":["100 pages per document","Max upload size: 15MB","50 questions per day"]},{"plan":"Premium","price":14.99,"billing_cycle":"Monthly","original_price":14.99,"discount":"","features":["2500 pages per document","Max upload size: 30MB","GPT-3.5 and GPT-4 model access"]},{"plan":"Pro","price":19.99,"billing_cycle":"Monthly","original_price":19.99,"discount":"","features":["6000 pages per document","Max upload size: 90MB","Unlimited questions and conversations per day"]}]}', 'AskYourPDF is an AI tool that revolutionizes the way users interact with PDF documents. By enabling conversational interactions with PDFs, users can engage in insightful conversations, save time, and learn playfully. With features like smart navigation, interactive chatbots, and the ability to chat with multiple documents simultaneously, AskYourPDF enhances productivity and efficiency in document management.', NULL, '["Document Management","Productivity","Education"]');
