INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('890a97ba-e250-445d-9321-9e0cd3d7da90', '2024-04-12T15:27:00.479Z', 'Automatica', 'https://www.automatica.xyz/', 'images/automatica.webp', 'images/automatica-icon.webp', '{"what":"Automatica is an AI-powered tool designed to deliver qualified leads to users on a weekly basis, reducing repetitive work by automating cold outreach personalization and optimizing meeting bookings.","who":"Automatica is for founders, operators, and sales professionals looking to increase conversions, save time, and lower costs in their lead generation and outreach efforts.","best_features":["Custom lead qualification through intricate question-based research","Hyper-personalized email outreach at scale with user-specified instructions","24/7 operating AI Assistants that free users from repetitive tasks"],"pricing":[]}', 'Automatica is an AI-powered tool that helps users get qualified leads weekly, custom qualify leads, scale personalized outreach, and book meetings faster. It leverages AI capabilities to automate cold outreach personalization and optimize meeting bookings by overcoming objections. The tool also includes 24/7 AI Assistants to handle repetitive tasks and seamlessly integrates into existing systems for enhanced efficiency.', NULL, '["Lead Generation","Sales Tools","Automation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f8ca2a1d-31d6-45f1-b217-340769e4ca62', '2024-04-12T15:27:00.479Z', 'Automix.ai', 'https://automix.ai/', 'images/automix-ai.webp', 'images/automix-ai-icon.webp', '{"what":"Automix.ai is an AI-Powered Resume Review & Mock Interview tool designed to assist job seekers in improving their chances of landing their dream career.","who":"This product is for job seekers looking to enhance their resume and interview skills, as well as recruiters seeking to identify top talent and improve hiring decisions.","best_features":["AI-powered resume reviews with detailed feedback and suggestions for improvement","Mock interview practice sessions with suggestions for improvement","COREPUS feature for recruiters to analyze multiple resumes and identify top talent"],"pricing":[]}', 'Automix.ai is an AI-Powered Resume Review & Mock Interview tool that offers resume reviews, mock interview practice sessions, and COREPUS feature for recruiters. It aims to provide valuable insights to job seekers and recruiters to enhance resume and interview skills, leading to better job prospects and improved hiring decisions.', NULL, '["AI Tools","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f4a79e1a-0339-4432-8d68-035c563e5542', '2024-04-12T15:27:00.479Z', 'Automorphic', 'https://automorphic.ai/', 'images/automorphic.webp', 'images/automorphic-icon.webp', '{"what":"Automorphic is a tool called Conduit that enables the infusion of knowledge into language models. It overcomes the limitations of prompt stuffing by allowing fine-tuning of language models, training adapters for behavior or knowledge, and combining them dynamically.","who":"Automorphic is designed for developers, data scientists, AI researchers, and businesses looking to enhance the performance of their language models, streamline model deployment, and protect against adversarial attacks.","best_features":["Fine-tuning of language models for behavior or knowledge","Dynamic combination of adapters for model enhancement","Aegis firewall tool for protection against adversarial attacks"]}', 'Automorphic is a tool that includes Conduit for enhancing language models, TREX for data conversion, and Aegis for firewall protection. It enables rapid iteration on models, human-in-the-loop feedback, and model deployment efficiency. The Automorphic Hub provides access to shared models, while TREX offers customizable data conversion. Aegis defends against prompt injections, PII leakage, and toxic language, continuously improving its detection capabilities.', NULL, '["Data Tools","Cybersecurity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('02ff3751-472e-4c34-ab94-d23234016fed', '2024-04-12T15:27:00.480Z', 'Autoname', 'https://www.autoname.org/', 'images/autoname.webp', 'images/autoname-icon.webp', '{"what":"Figma Autoname is a free and open-source plugin for Figma that uses AI to automatically rename layers and components with one click. It can detect and prevent renaming of components and instances, saving time in design work.","who":"This product is designed for designers and professionals who use Figma for their design work and want to streamline the process of renaming layers and components.","best_features":["Blazing fast renaming of all Figma design layers in one click.","Smart detection and prevention of renaming for components and instances.","Community-driven, free, and open-source plugin with positive reviews."],"pricing":[]}', 'Figma Autoname is a plugin for Figma that uses AI to automatically rename layers and components with one click. It saves time in design work by quickly renaming all layers in a design. The plugin is free, open-source, and community-driven, with positive reviews from users.', NULL, '["AI Tools","Productivity Tools"]');
