INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e66a1fe4-e219-43d2-bb2c-685979e01916', '2024-04-12T15:27:00.480Z', 'AutoPredict', 'https://autopredict.co.uk/', 'images/autopredict.webp', 'images/autopredict-icon.webp', '{"what":"AutoPredict is an AI tool that predicts the lifespan of a car by analyzing millions of data points. It generates accurate predictions and statistics as well as historical MOT data. It also offers an API for businesses and provides insights and statistics on its blog.","who":"AutoPredict is for car owners who want to have a better understanding of how long their car may last and for businesses looking to integrate accurate car lifespan predictions into their services.","best_features":["Accurate lifespan predictions based on millions of data points","Historical MOT data analysis for additional insights","API for businesses to integrate predictions and statistics"]}', 'AutoPredict is an AI tool that uses artificial intelligence to predict the lifespan of a car by analyzing over 100 million data points. It provides accurate estimates of a car''s life and offers statistics and insights through its blog. The tool also offers an API for businesses to integrate predictions and statistics into their services.', NULL, '["Automotive","Data Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9ad3e4cb-d490-4813-b0c4-79b4ca54d237', '2024-04-12T15:27:00.480Z', 'AutoProfile', 'https://autoprofile.me/', 'images/autoprofile.webp', 'images/autoprofile-icon.webp', '{"what":"AutoProfile is an online tool that simplifies the process of creating and managing online profiles for personal branding and professional networking. It automatically generates comprehensive profiles by scanning publicly available information and allows users to customize them easily.","who":"AutoProfile is for professionals looking to expand their networks and individuals wanting to create a cohesive online identity. It is suitable for anyone seeking to streamline their online presence for personal or professional reasons.","best_features":["Automatic Generation: State-of-the-art algorithms scan publicly available information to create a profile in real-time.","Profile Enhancement: Users can add a personal touch by answering simple questions to customize their profiles.","Privacy-Centric: Users have control over who can view their profiles by privately sharing the profile link."],"pricing":[]}', 'AutoProfile is an online tool that helps users effortlessly create and customize professional, personal, or social profiles. It aims to improve careers, enhance social media presence, and simplify online profile management. The tool automatically generates profiles by scanning publicly available information and allows for easy customization through a user-friendly interface. Privacy is prioritized, and users can control who views their profiles. AutoProfile is versatile and suitable for professionals and individuals looking to create a cohesive online identity.', NULL, '["Personal Branding","Online Profile Management"]');
