INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a00b2b54-a7e7-4129-aaec-a4f13cdb2981', '2024-04-12T15:27:00.490Z', 'Baby AGI', 'https://github.com/yoheinakajima/babyagi', 'images/baby-agi.webp', 'images/baby-agi-icon.svg', '{"what":"Baby AGI is an AI-powered task management system that uses natural language processing and AI to create and execute tasks based on previous task results and predefined objectives. It is designed to simplify task management and improve productivity.","who":"This product is ideal for individuals or teams looking to streamline their task management process, prioritize tasks effectively, and leverage AI technology to enhance their workflow.","best_features":["Utilizes OpenAI''s API for task completion and natural language processing","Allows for task prioritization and reprioritization based on objectives and results","Stores task results in Chroma/Weaviate for context and future reference"],"pricing":[]}', 'Baby AGI is an AI-powered task management system that leverages natural language processing and AI technology to create, prioritize, and execute tasks efficiently. It integrates with OpenAI''s API for task completion, enables task prioritization based on objectives, and stores task results in Chroma/Weaviate for context. The tool is open-source and continuously updated by the development community.', NULL, '["AI Assistance","Task Management","Open-source"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('72ffcba6-6e49-490d-b719-8301226b5b88', '2024-04-12T15:27:00.490Z', 'Baby Maker & Baby Face Generator', 'https://www.babyfacegenerator.com', 'images/baby-maker-&-baby-face-generator.webp', 'images/baby-maker-&-baby-face-generator-icon.webp', '{"what":"The Baby Maker & Baby Face Generator is an AI-powered tool that predicts the potential appearance of a future baby by analyzing facial features from two partner''s photos.","who":"This product is for individuals in serious relationships looking to visualize their future family or for those who are simply curious about the outcome. It provides a fun and entertaining way to create digital baby portraits.","best_features":["AI-powered tool that analyzes 70 facial features to generate a realistic image of a potential offspring.","Ability to upload photos or choose celebrity faces for customization.","Options to personalize the generated baby''s appearance with features like skin tone and gender preferences."]}', 'Baby Face Generator is an AI-powered tool that uses facial analysis to predict what a future baby may look like based on two partner''s photos. It offers customization options such as choosing celebrity faces or adjusting skin tone and gender preferences. Whether for serious relationship planning or simple curiosity, this tool provides a fun and interactive way to visualize potential offspring.', NULL, '["Art Generation","Entertainment"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7826637e-7ebf-45b1-b4ec-1fc7309e475e', '2024-04-12T15:27:00.490Z', 'BabyBeeAGI', 'https://replit.com/@YoheiNakajima/BabyBeeAGI', 'images/babybeeagi.webp', 'images/babybeeagi-icon.webp', '{"what":"BabyBeeAGI is an AI tool that is a modified version of OG BabyAGI, which is a lightweight implementation of Artificial General Intelligence (AGI) in Python. It is intended for builders to experiment with AGI development and is shared for those who want to tinker and experiment with the technology.","who":"BabyBeeAGI is for builders and developers who are interested in experimenting with Artificial General Intelligence (AGI) development. It is not for users who expect immediate results or need a complete solution, but rather for those who enjoy tinkering and exploring the possibilities of AGI.","best_features":["Modified version of OG BabyAGI for experimentation with AGI development.","Lightweight implementation in Python with around 300 lines of code.","Shared tool for builders who want to tinker and experiment with AGI technology."],"pricing":""}', 'BabyBeeAGI is an AI tool that allows builders and developers to experiment with Artificial General Intelligence (AGI) development. It is a modified version of OG BabyAGI, implemented in Python with around 300 lines of code. The tool is intended for experimentation and tinkering, not for users seeking immediate solutions. Users can fork BabyBeeAGI on Replit, add their OpenAI API Key, update the OBJECTIVE variable, and run the tool to explore AGI capabilities. Made with love by @yoheinakajima, BabyBeeAGI is available on Github for those interested in AGI development.', NULL, '["Artificial Intelligence","Developer Tools","Experimentation"]');
