INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cb57351f-5fb1-4c4c-af81-af50949c4d41', '2024-04-12T15:27:00.514Z', 'Blink', 'https://mmntm.me/blink', 'images/blink.webp', 'images/blink-icon.webp', '{"what":"Blinkn is an AI shopping assistant tool called ChatGPT specifically designed for ecommerce stores. It acts as a chat widget that can understand customer queries and provide relevant product recommendations, comparisons, and information.","who":"Blinkn is for ecommerce store owners looking to enhance their customers'' shopping experience by providing intelligent product recommendations and a user-friendly chat interface.","best_features":["AI-powered product recommendations and comparisons","Seamless integration with popular ecommerce platforms","Customization options for branding alignment"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Limited user interactions","Core AI-Chat functionality"]},{"plan":"Growth Plan","price":"Custom Quote","billing_cycle":"Monthly","original_price":"Custom Quote","discount":"N/A","features":["Increased user interactions","Customizable chat interface","Analytics and insights","Scalable user interactions","Priority support"]}]}', 'Blinkn is an AI shopping assistant tool designed for ecommerce stores. It offers AI-powered product recommendations, comparisons, and information through a user-friendly chat interface. With seamless integration and customization options, Blinkn enhances the shopping experience for customers. It supports multiple languages, integrates with live chat systems, and offers different pricing plans to cater to various needs.', NULL, '["AI Assistance","Chat Solutions","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('34ee070c-31b9-440a-a4bc-78d42f25be5c', '2024-04-12T15:27:00.514Z', 'Blinkn.shop', 'https://blinkn.shop/', 'images/blinkn-shop.webp', 'images/blinkn-shop-icon.webp', '{"what":"Blink & Shop is an AI shopping assistant powered by ChatGPT, offering a convenient and efficient way for users to browse and purchase a wide range of products.","who":"Blink & Shop is designed for users looking for a personalized shopping experience with the help of an AI assistant, catering to individuals seeking quick and accurate assistance in their online shopping journey.","best_features":["AI-powered shopping assistant for personalized recommendations and product search","Chat-based interface for easy communication with the virtual assistant","Contact support feature for addressing user inquiries and issues"],"pricing":[]}', 'Blink & Shop is an AI shopping assistant that provides users with a chat-based interface to browse and purchase various products. The tool offers personalized shopping experiences, quick assistance, and accurate product information through its AI-powered virtual assistant.', NULL, '["AI Assistance","Chat Solutions","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('56f8546c-3ff4-4c65-9123-709ee753b0ad', '2024-04-12T15:27:00.514Z', 'Blinkops', 'https://www.blinkops.com/', 'images/blinkops.webp', 'images/blinkops-icon.webp', '{"what":"Blinkops is a security automation tool that allows users to automate various security tasks and workflows with ease. It offers integrations with leading security vendors and provides pre-built workflows for quick automation.","who":"Blinkops is designed for security teams and business leaders who are looking to enhance their security operations and streamline their incident response processes.","best_features":["Automate any task or workflow with ease","Thousands of pre-built integrations across leading security vendors","No-code, low-code, or code-based workflows for quick time to value"],"pricing":[]}', 'Blinkops is a security automation tool that simplifies the process of automating security tasks and workflows. With integrations across leading security vendors and a wide range of pre-built workflows, users can enhance their security operations and streamline incident response processes. Whether it''s SOC & Incident Response, Vulnerability Management, IT Security, Cloud Security, Identity & Access Management, or Governance, Risk & Compliance, Blinkops offers automation solutions for all security needs.', NULL, '["Automation","Incident Response","Compliance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('58bac9e6-c7c9-42a0-ba3d-204df51a8aff', '2024-04-12T15:27:00.514Z', 'Blizzy', 'https://www.blizzy.app/', 'blizzy.webp', 'images/blizzy-icon.webp', '{"what":"Blizzy is an AI tool that enables users to have meaningful conversations with their data. It allows chatting with files, accessing the internet securely, and creating a personalized knowledge vault by uploading documents.","who":"Blizzy is designed for individuals and teams who want to leverage AI to enhance their productivity, decision-making, and content creation processes. It caters to a wide range of users, from professionals in various industries to students and researchers.","best_features":["Personalized Knowledge Vault: Blizzy allows users to upload and organize their files, creating a centralized hub for their data. This feature enables easy access and retrieval of information, improving productivity and decision-making.","Secure Internet Access: Blizzy provides a secure way for users to access the internet, ensuring the privacy and confidentiality of their data.","Comprehensive AI Capabilities: Blizzy offers a range of AI-powered features, including ready-made prompts for marketing, AIDA rewrite for copy transformation, and article creation based on SEO keywords. These features help users streamline their content creation and optimization processes."],"pricing":[{"plan":"Free Starter Plan","price":0,"billing_cycle":"N/A","features":["Basic file management and organization","Limited internet access","Basic AI-powered features"]},{"plan":"Pro Plan","price":9.99,"billing_cycle":"Monthly","features":["Unlimited file storage and organization","Secure internet access","Advanced AI-powered features","Personalized knowledge vault","Priority support"]},{"plan":"Enterprise Plan","price":49.99,"billing_cycle":"Monthly","features":["Dedicated account manager","Customized AI capabilities","Enterprise-level security and compliance","Integrations with third-party tools","Comprehensive training and support"]}]}', 'Blizzy is an AI-powered tool that enables users to have meaningful conversations with their data, access the internet securely, and create a personalized knowledge vault. It offers a range of features to enhance productivity, content creation, and decision-making.', NULL, '["Productivity Tools","Content Creation","Data Management"]');
