INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0beaf9cd-237c-4124-8335-b087c03e78ae', '2024-04-12T15:27:00.537Z', 'Brief News', 'https://www.brief.news/', 'images/brief-news.webp', 'images/brief-news-icon.webp', '{"what":"Brief by Upnext is an AI-powered tool that delivers a personalized news digest via email. It curates top stories of the day based on the user''s interests and summarizes articles from various sources to provide a comprehensive daily briefing.","who":"This product is designed for individuals who want to stay informed and updated with relevant news without the hassle of searching through multiple sources. It caters to a wide range of topics to suit different preferences.","best_features":["Personalized news digest tailored to user''s interests","AI-powered curation for efficiency and relevance","Option to choose between curated top stories or a list of all stories"]}', 'Brief by Upnext is an AI-powered tool that delivers a personalized news digest via email. It caters to an extensive range of topics and curates top stories of the day according to the user''s area of interest. The tool is designed to keep users informed and updated with relevant news, removing the need to sift through multiple sources. Users can choose the format of their brief between a curated set of top stories or a list of all stories. Brief by Upnext employs AI technology to make the curation and delivery process more efficient.', NULL, '["AI-Powered","Personalized","Digest"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('49faa407-d2c8-4985-a271-ee9aa7cd4844', '2024-04-12T15:27:00.538Z', 'BriefAI', 'https://briefai.netlify.app/', 'images/briefai.webp', 'images/briefai-icon.webp', '{"what":"Brief.Ai is an open-source article summarizer tool that utilizes advanced natural language processing capabilities to transform lengthy articles into clear and concise summaries. It aims to simplify reading by providing users with key points without the need to read the entire text.","who":"Brief.Ai is designed for individuals who need to quickly grasp the essential information from lengthy articles without investing significant time and effort. It caters to a wide range of users, including students, researchers, professionals, and anyone looking to enhance their reading experience.","best_features":["Utilizes GPT-4 for advanced natural language processing capabilities","Provides clear and concise summaries of lengthy articles","Encourages collaboration and customization through its open-source nature"]}', 'Brief.Ai is an open-source article summarizer tool powered by GPT-4, designed to simplify reading by transforming lengthy articles into clear and concise summaries. Users can quickly obtain key points without reading the entire text, enhancing the reading experience and saving time. The tool eliminates the need to sift through extensive texts by condensing content into easily comprehensible summaries. Brief.Ai encourages collaboration and modification, catering to various usage scenarios such as academic research, content curation, and general information consumption.', NULL, '["Text-to-Summary","Natural Language Processing","Open-Source Tool"]');
