INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8a0836c4-18c0-44a3-adef-7bcada1d0588', '2024-04-12T15:28:12.566Z', 'Chat Booper', 'https://chat.booper.dev/', 'images/chat-booper.webp', 'images/chat-booper-icon.svg', '{"what":"Booper is an AI tool that enables users to embed a custom-designed chat widget on their website, allowing them to communicate with their users directly from Discord.","who":"This product is for website owners who want to engage with their users through a live chat interface connected to Discord.","best_features":["User-friendly and easy to integrate chat widget","Customizable appearance with sleek design","Supports emoji reactions for engaging interactions"],"pricing":[]}', 'Booper is an AI tool that allows users to embed a chat widget on their website, connecting them directly to Discord for communication. The chat widget is user-friendly, customizable, and supports emoji reactions for engaging interactions. With easy integration and seamless communication, Booper provides a convenient solution for website owners to interact with their users in real-time.', NULL, '["AI Tools","Web Development"]');
