INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f3e1db6c-dd97-456d-8732-6df981900daa', '2024-04-12T15:28:12.566Z', 'Chat Documents', 'https://chatdocuments.ai/', 'images/chat-documents.webp', 'images/chat-documents-icon.webp', '{"what":"ChatDocuments is an AI-powered app that enables users to engage in interactive chats with various document formats, including PDF, PPTX, XLSX, and DOCX.","who":"This product is designed for individuals and professionals looking to actively engage with their documents through a seamless chat interface, backed by AI technology.","best_features":["AI Powered Document Interaction: Efficient and effective communication with documents.","Multi-Languages Support: Seamless interaction with documents regardless of language.","Secure and Private: Emphasizing security and privacy for users'' data."],"pricing":[{"plan":"Paid subscription","price":14.99,"billing_cycle":"monthly","original_price":0,"discount":"Free access to 3 documents","features":["AI Powered Document Interaction","Secure and Private","Multi-Languages Support"]}]}', 'ChatDocuments is an AI-powered app that allows users to interact with PDF, PPTX, XLSX, and DOCX files through a chat interface. It supports multiple languages, ensures security and privacy, and offers free access to three documents. Users can upgrade to a paid subscription for more extensive functionality at $14.99 per month.', NULL, '["Document Interaction","Language Support","Security and Privacy"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('37549f59-d345-4fa0-8528-ec247fe4e9c1', '2024-04-12T15:28:12.567Z', 'Chat Ever After', 'https://chateverafter.com/', 'images/chat-ever-after.webp', 'images/chat-ever-after-icon.svg', '{"what":"Chat Ever After is an AI-powered grief prevention therapy tool that allows users to engage in simulated conversations with their deceased loved ones.","who":"This product is for individuals looking to connect with departed loved ones and seeking a unique and immersive experience in dealing with grief.","best_features":["AI-powered technology recreates conversations with deceased loved ones","Users have control over the tone of the chats","Available on all chat platforms for natural conversations"],"pricing":[{"plan":"General Base Model","price":19.99,"billing_cycle":"monthly","original_price":19.99,"discount":"","features":["50 Chats Per Month"]},{"plan":"Fine Tuned Model","price":30.99,"billing_cycle":"monthly","original_price":30.99,"discount":"","features":["500 Chats Per Month"]},{"plan":"Fully Trained Model","price":70.99,"billing_cycle":"monthly","original_price":70.99,"discount":"","features":["Unlimited Chats"]}]}', 'Chat Ever After is an AI-powered grief prevention therapy tool that allows users to engage in simulated conversations with their deceased loved ones. The tool generates chat renditions mimicking historic chat data, with users having control over the tone of the chats. It offers multiple pricing plans to meet different needs and emphasizes privacy and data protection.', NULL, '["AI Tools","Therapy","Grief Prevention"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f0893e1d-02e1-4d3c-8b5a-6e8c82adc1fa', '2024-04-12T15:28:12.567Z', 'Chat-GPT Pictures', 'https://chat-gpt.pictures', 'images/chat-gpt-pictures.webp', 'images/chat-gpt-pictures-icon.webp', '{"what":"ChatGPT Image Generator is an AI-powered tool that seamlessly merges language models and image generation to create contextually relevant and visually appealing images.","who":"This product is designed for artists, designers, content creators, and anyone looking to effortlessly generate unique visuals for various purposes.","best_features":["Detailed image generation for blogs, posts, and more","Incorporates text, numbers, and hands into images for added customization","Improved contextual understanding of image content for accurate visual representation"],"pricing":[{"plan":"Pack One","price":5,"billing_cycle":"month","original_price":5,"discount":"","features":["Single user access","Unlimited projects","Download prototypes"]},{"plan":"Pack Two","price":9,"billing_cycle":"month","original_price":9,"discount":"","features":["5 Users access","Unlimited projects","Download prototypes"]}]}', 'ChatGPT Image Generator seamlessly merges AI language models and image generation to create contextually relevant and visually appealing images. It caters to various needs like product mockups, social media graphics, and concept art with features like detailed image generation, support for text and numbers incorporation, and improved contextual understanding.', NULL, '["Image Generation","Art Generation","Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a276ecfb-0b65-4795-85b5-71416485f08c', '2024-04-12T15:28:12.567Z', 'Chat LLaMA', 'https://serp.ai/tools/chat-llama/', 'images/chat-llama.webp', 'images/chat-llama-icon.svg', '{"what":"Chat LLaMA is a tool that offers a novel approach to fine-tuning large language models (LLMs) used in natural language processing (NLP) tasks. It leverages low-rank approximation techniques to make the adaptation process more efficient and cost-effective while maintaining the impressive capabilities of LLMs.","who":"Chat LLaMA is designed for developers, data scientists, and NLP practitioners who work with large language models and want to improve their efficiency and sustainability. It is also beneficial for businesses looking to customize LLMs for specific tasks.","best_features":["Efficient and cost-effective fine-tuning of large language models","Utilizes low-rank approximation techniques for faster adaptation","Maintains the impressive capabilities of LLMs while reducing computational resources and time"]}', 'Chat LLaMA is a tool included in the LoRA (Low-Rank Adaptation) solution, offering a groundbreaking method for fine-tuning large language models used in natural language processing tasks. It simplifies the adaptation process by leveraging low-rank approximation techniques, such as Singular Value Decomposition (SVD) or Truncated SVD, to make it more efficient and cost-effective. With Chat LLaMA, users can customize LLMs for specific tasks, improving their accuracy and relevance without sacrificing performance.', NULL, '["NLP","Machine Learning","Software Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9f738609-5e91-4895-a331-674233f41968', '2024-04-12T15:28:12.567Z', 'Chat Whisperer', 'https://chatwhisperer.ai/', 'images/chat-whisperer.webp', 'images/chat-whisperer-icon.webp', '{"what":"Chat Whisperer is an advanced AI chatbot tool designed for websites. Its primary function is to assist with customer support services while also helping to drive sales by offering product recommendations.","who":"Chat Whisperer is designed for businesses looking to enhance customer interaction, increase sales opportunities, improve employee productivity, and provide efficient support experiences.","best_features":["Seamless integration with websites to enhance customer interaction and potentially increase sales opportunities.","Utilizes chat GPT for identifying upselling opportunities to maximize sales potential.","Can be used for various specific areas such as sales AI chatbot, topic research AI chatbot, customer engagement AI chatbot, and more."]}', 'Chat Whisperer is an advanced AI chatbot tool designed for websites. It integrates with a website to enhance customer interaction, drive sales, and improve employee productivity. The platform offers efficient support by connecting users to a library of information, leveraging product upselling, providing instant access to information, and offering seamless support experiences. It also extends its services to specific areas like sales AI chatbot, topic research AI chatbot, customer engagement AI chatbot, and more.', NULL, '["AI Agents","Customer Support","E-commerce"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('72b9a334-b4a4-4012-9986-0f021ea6fe1a', '2024-04-12T15:28:12.568Z', 'Chat with Jinnah', 'https://www.messengerx.io/jinnah', 'images/chat-with-jinnah.webp', 'images/chat-with-jinnah-icon.webp', '{"what":"Chat with Jinnah is an AI chatbot tool that allows users to converse with the voice of Pakistan''s founder, Muhammad Ali Jinnah. It generates responses in English, creating a unique experience for users to interact with historical figures.","who":"This product is for history enthusiasts, students studying Pakistan''s history, individuals interested in AI technology, and anyone curious about conversing with historical figures in a digital format.","best_features":["Unique experience of chatting with Muhammad Ali Jinnah through AI technology.","Engaging conversations in English that simulate interactions with the historical figure.","Educational tool for learning about Pakistan''s history and the perspective of its founder."],"pricing":[]}', 'The Jinnah AI tool is a chatbot that can generate responses in the voice of Pakistan''s founder, Muhammad Ali Jinnah. Users can chat with Jinnah in English, creating a unique and educational experience. Please note that the responses are AI-generated and may not always be accurate or truthful.', NULL, '["Historical Figures","Chatbot","Educational Tool"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7310d84b-1135-4e26-8fe3-032e47e4a3cd', '2024-04-12T15:28:12.568Z', 'Chat With Media', 'https://www.aichatwithmedia.com/', 'images/chat-with-media.webp', 'images/chat-with-media-icon.webp', '{"what":"Chat With Media (CWM) is an open-source software designed to enable interactive communication with PDF files. The tool works by allowing the user to upload a file and start a conversation instantly, making engagement seamless and efficient.","who":"This product is for individuals and businesses looking to engage in interactive communication with PDF files, making file engagement more conversational and user-friendly.","best_features":["Seamless communication with PDF files","User-friendly design for quick and easy setup","Option to choose between free and pro plans for flexibility"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Basic file upload and chat functionality","Limited support options"]},{"plan":"Pro Plan","price":9.99,"billing_cycle":"Monthly","original_price":9.99,"discount":"N/A","features":["Advanced file processing capabilities","Enhanced user support","Additional customization options"]}]}', 'Chat With Media (CWM) is an open-source software that enables users to have interactive conversations with PDF files. Users can sign up for an account, upload a file, and start chatting instantly. The tool processes the uploaded files to make them chat-ready, providing a seamless and efficient communication experience. With options for free and pro plans, users can choose the best fit for their needs. Chat With Media aims to transform how individuals engage with media by introducing a conversational approach to file interaction.', NULL, '["File Engagement","Interactive Communication","User-Friendly Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0c2a6e43-5064-4d8c-b4d7-30af59f74c58', '2024-04-12T15:28:12.568Z', 'Chat With RTX', 'https://www.nvidia.com/en-us/ai-on-rtx/chat-with-rtx-generative-ai/', 'images/chat-with-rtx.webp', 'images/chat-with-rtx-icon.svg', '{"what":"Chat With RTX is a demo app provided by NVIDIA that allows users to personalize a Generative Pretrained Transformer (GPT) large language model chatbot. Users can configure the chatbot to connect to their own content, such as documents, notes, and videos, for personalized interactions.","who":"This product is designed for individuals and businesses looking for a customizable AI solution for chatbot interactions based on a wide range of content. It is especially useful for those who require personalized interaction with user-specific data.","best_features":["Personalization: Users can connect the chatbot to their own content for tailored interactions.","AI Technology: Utilizes Generative Pretrained Transformer (GPT) large language model for advanced chatbot capabilities.","RTX Acceleration: Takes advantage of NVIDIA RTX technology for fast and secure processing."],"pricing":[]}', 'Chat With RTX is a tool provided by NVIDIA that allows users to personalize a Generative Pretrained Transformer (GPT) large language model chatbot. It offers a customizable AI solution for those who need a chatbot that can process and interact based on a wide range of content. The tool exploits the power of the NVIDIA RTX technology, making it ideal for personalized interactions with user-specific data.', NULL, '["Chat Solutions","AI Agents","Artificial Intelligence"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('79e50cb6-0907-46a9-85ca-8b04234a4706', '2024-04-12T15:28:12.568Z', 'Chat Worm', 'https://chatworm.com/', 'images/chat-worm.webp', 'images/chat-worm-icon.webp', '{"what":"Chat Worm is a chatGPT client that enables direct chatting via API, offering faster response times, cost-effectiveness, multiple models, customization options, and open-source accessibility.","who":"Chat Worm is designed for businesses, developers, and individuals looking for a reliable and efficient chat solution for various purposes.","best_features":["Faster response times for efficient communication","Cost-effectiveness for budget-friendly usage","Multiple models and customization options for tailored chat experiences"],"pricing":[]}', 'Chat Worm is a versatile chatGPT client that provides direct chatting capabilities via API. It offers faster response times, cost-effectiveness, multiple models, customization options, and open-source accessibility. Available as a web app, Android app, and Windows app, Chat Worm is suitable for businesses, developers, and individuals seeking a reliable chat solution.', NULL, '[""]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b17e28a9-57c7-4fd9-a221-fbbb5b4e6d9b', '2024-04-12T15:28:12.568Z', 'Chat Z', 'https://www.zitti.com/chat-z', 'images/chat-z.webp', 'images/chat-z-icon.webp', '{"what":"Chat Z is an AI tool by Zitti that offers powerful automation for food business procurement. It acts as a virtual Sous Chef, optimizing purchasing decisions and maximizing cost savings.","who":"Chat Z is designed for restaurants and food businesses looking to make smarter ordering decisions and gain insights into their procurement data.","best_features":["Real-time price comparison across multiple vendors","Automated order placement based on user preferences","Tailored suggestions to reduce expenses and increase savings"],"pricing":[]}', 'Chat Z is an AI tool that serves as a virtual Sous Chef for food businesses, helping them optimize purchasing decisions, maximize cost savings, and gain insights into procurement data.', NULL, '["AI Tools","Food & Drink"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('63480199-fb1f-423a-8e35-e637ceb26588', '2024-04-12T15:28:12.569Z', 'Chat2code.ai', 'https://www.chat2code.ai/', 'images/chat2code-ai.webp', 'images/chat2code-ai-icon.webp', '{"what":"CodeCompanion.AI is an AI-powered coding tool that serves as a loyal companion for programmers, offering autopilot for coding, natural language to SQL conversion, data analytics features, and more.","who":"CodeCompanion.AI is designed for programmers, developers, and individuals looking to streamline their coding process, automate tasks, and simplify data analytics.","best_features":["Autopilot for coding across any platform or language","Natural language to SQL feature for seamless database integration","Data analytics made easy with plain English to chart conversion"],"pricing":[{"plan":"monthly","price":5,"billing_cycle":"per month","original_price":0,"discount":"Free version available with OpenAI API key","features":["Autopilot coding","Data analytics","Natural language to SQL"]}]}', 'CodeCompanion.AI, formerly known as Chat2Code.ai, is an AI tool that simplifies coding with autopilot features, natural language to SQL conversion, and data analytics capabilities. It offers a Better ChatGPT experience, data privacy assurance, and seamless app creation across platforms and languages. The tool is priced at $5 per month with a free version available. Users can code using only natural language and enjoy hassle-free BI tools for data exportation.', NULL, '["Coding Assistance","Data Analysis","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('808a41c2-0cec-48fd-8de3-61c89c2465ad', '2024-04-12T15:28:12.569Z', 'Chat2Code.dev', 'https://chat2code.dev/', 'images/chat2code-dev.webp', 'images/chat2code-dev-icon.webp', '{"what":"Chat2Code.dev is an AI-driven tool that enables interactive coding by generating, executing, and updating React components in real-time. It simplifies the coding process by transforming users'' words into functional code components, eliminating the need for manual coding and accelerating development time.","who":"Chat2Code.dev is designed for developers looking for an intuitive and efficient coding tool that can generate React components, integrate with various frameworks, and offer premium features for enhanced functionality.","best_features":["Real-time generation, execution, and updating of React components","Integration with multiple frameworks such as React, Vue, Angular, Svelte, and Vanilla","Premium features for advanced functionality and support"]}', 'Chat2Code is an AI-driven tool that enables interactive coding by generating, executing, and updating React components in real-time. With Chat2Code, programming becomes effortless as it allows users to step into the future of coding. The tool integrates with various frameworks such as React, Vue, Angular, Svelte, and Vanilla, offering flexibility to choose the desired framework through the settings menu. Chat2Code uses Tailwind CSS to quickly build components, ensuring efficient development.', NULL, '["Coding Assistance","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e2239b2e-bd8e-4ab7-9d77-3ec0d82a2c65', '2024-04-12T15:28:12.569Z', 'Chat2CSV', 'https://chat2csv.com/', 'images/chat2csv.webp', 'images/chat2csv-icon.webp', '{"what":"Chat2CSV is a data visualization tool that allows users to transform CSV files into various types of charts using natural language commands. The platform emphasizes simplicity and efficiency, eliminating the need for complex coding or scripting.","who":"Chat2CSV is designed for individuals and businesses looking to easily visualize their data without the need for coding skills. It is suitable for data analysts, researchers, marketers, and anyone who wants to gain insights from their CSV files through intuitive chart creation.","best_features":["Intuitive Natural Language: Users can create charts by simply providing instructions in plain English, making data visualization accessible to all.","Smart AI Support: The platform''s AI not only understands user commands but also provides suggestions on the most suitable charts for data analysis.","Wide Variety of Charts: From pie charts to scatter plots, users have access to a diverse range of chart types to effectively visualize their data."]}', 'Chat2CSV is a data visualization tool that simplifies the process of creating charts from CSV files using natural language commands. The platform''s AI support enhances data analysis by providing suggestions on the best chart types. With a focus on security and privacy, users can visualize their data with confidence. Free access with minor limitations is available, while a premium plan offers advanced features for a more comprehensive data visualization experience.', NULL, '["AI Tools","Data Visualization","Productivity Tools"]');
