INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('de1646bc-9e69-4717-853d-a5164b393b65', '2024-04-12T15:28:12.602Z', 'Clip Panda', 'https://www.clippanda.com/', 'images/clip-panda.webp', 'images/clip-panda-icon.webp', '{"what":"Clip Panda is an AI-driven tool designed to simplify the process of generating videos from specific prompts input by the user. It uses innovative AI technology to create videos based on stock video footage and music, prompted by the users'' inputs.","who":"Clip Panda is for content creators, social media managers, marketers, and anyone looking to streamline the video editing process and create stunning videos quickly. It is also suitable for individuals who want to easily generate videos for personal use or business purposes.","best_features":["Streamlines video editing process, allowing users to produce videos in seconds","Uses AI technology to create videos based on user inputs","Provides voice-over using GPT-4 and text-to-voice technology"],"pricing":[]}', 'Clip Panda is an AI-driven tool that simplifies video editing by generating videos based on user prompts. It uses AI technology to create videos with stock footage, music, and voice-overs. The user-friendly interface allows for quick and easy video creation for a variety of purposes.', NULL, '["Video Editing","AI Solutions","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4cf25ae9-a86f-408f-ac17-0c9755618a5a', '2024-04-12T15:28:12.602Z', 'ClipBuddy', 'https://clipbuddy.io/', 'images/clipbuddy.webp', 'images/clipbuddy-icon.webp', '{"what":"ClipBuddy is a generative AI video tool designed to convert long videos into short clips for platforms like TikTok, Instagram, and YouTube Shorts.","who":"ClipBuddy is for content creators looking to repurpose their existing videos into engaging and attention-grabbing viral clips without the need for extensive editing.","best_features":["Auto Curation: AI selects the most captivating moments from videos.","Auto Captioning: AI adds dynamic captions to enhance engagement.","Resizing: Auto-adjust videos to the ideal 9:16 ratio for perfect fit across all devices."]}', 'ClipBuddy is a generative AI video tool that helps users transform lengthy videos into short, engaging clips tailored for social media platforms. It offers features like auto curation, auto captioning, and resizing to enhance content creation and audience engagement.', NULL, '["AI Tools","Video Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bbc08360-7dcc-40c9-af7e-0ddd9ec772fa', '2024-04-12T15:28:12.603Z', 'Clipcision', 'https://clipcision.com/', 'images/clipcision.webp', 'images/clipcision-icon.webp', '{"what":"Clipcision is an AI-powered YouTube management tool designed to optimize content management and workflow for YouTube channels.","who":"Clipcision is for content creators on YouTube looking to streamline their content creation process, attract more sponsors, and expand their audience.","best_features":["AI-trained modules for content optimization","Over 15 tools for YouTube content management and optimization","Increased productivity by up to 80% and 44% more viewers within 60 days"],"pricing":[]}', 'Clipcision is an AI-powered YouTube management tool that offers a comprehensive suite of AI tools to simplify and optimize the content creation process for YouTube channels. It provides over 15 tools for content management and optimization, including on-demand shorts editing, video script writing, and YouTube thumbnail editing. By leveraging AI technology, users can increase productivity, attract more sponsors, and expand their audience. Clipcision aims to save time, streamline processes, and improve content quality for content creators on YouTube.', NULL, '["AI Tools","Video Editing","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b76e7e48-2a6d-4e12-80c3-3adbb1440cd8', '2024-04-12T15:28:12.603Z', 'ClipDrop', 'https://clipdrop.co/', 'images/clipdrop.webp', 'images/clipdrop-icon.webp', '{"what":"Clipdrop is an AI tool that offers a range of image editing capabilities, including background removal, object removal, text generation, and image upscaling. It also provides an API for integration with other tools.","who":"Clipdrop is designed for individuals and businesses in various industries, including e-commerce, real estate, and marketing, who need quick and efficient image editing solutions.","best_features":["Automatic cleanup and removal of objects, people, text, and defects from photos","Background replacement with new backgrounds","Image upscaling and relighting capabilities"],"pricing":[]}', 'Clipdrop is an AI tool that can quickly and automatically clean up and remove objects, people, text, and defects from photos, as well as remove backgrounds and replace them with a new one. It can also improve image quality with relighting and upscaling capabilities. Clipdrop has an ecosystem of apps, plugins, and resources and uses powerful artificial intelligence.', NULL, '["AI Tools","Artificial Intelligence"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e3001743-73e9-41fe-959a-07486257dc13', '2024-04-12T15:28:12.603Z', 'Clipstudio', 'https://www.clipstudio.net/en/', 'images/clipstudio.webp', 'images/clipstudio-icon.webp', '{"what":"Clip Studio Paint is a powerful artist''s app for drawing and painting, offering a wide range of features suitable for various art forms such as concept art, character art, illustrations, comics, manga, and animations.","who":"Clip Studio Paint is designed for artists, illustrators, comic creators, animators, and anyone interested in digital art creation. It caters to beginners as well as seasoned professionals looking for a comprehensive tool for their artistic endeavors.","best_features":["Over 120,000 high-quality brushes and patterns for versatile art creation","Customizable 3D models and tools for advanced character design and posing","Community support and sharing platform for learning and inspiration"],"pricing":[{"plan":"Monthly Usage Plans","price":0.99,"billing_cycle":"per month","original_price":54,"discount":"","features":["Windows/macOS/iPad/iPhone/Galaxy/Android/Chromebook support"]},{"plan":"One-time Purchase","price":54,"billing_cycle":"one-time","original_price":54,"discount":"","features":["Windows/macOS support"]}]}', 'Clip Studio Paint is a versatile artist''s app that provides a wide range of tools and features for creating concept art, illustrations, comics, manga, and animations. With over 120,000 brushes, customizable 3D models, and a supportive community, it offers a comprehensive platform for artists of all levels.', NULL, '["Illustration","Comics","Animation"]');
