INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a1b34ae8-55c6-4b73-a964-e4b74f4cbc36', '2024-04-12T15:28:12.604Z', 'Clipwing', 'https://clipwing.pro/', 'images/clipwing.webp', 'images/clipwing-icon.webp', '{"what":"Clipwing is an AI-powered video processing tool designed to convert long videos into concise, short clips. It provides a simple-to-use interface where users only need to upload a video, and get a generated transcript from which they can highlight the needful text parts to create short clips.","who":"Clipwing is for content creators, social media influencers, educators, podcasters, and anyone looking to create engaging video content from longer videos.","best_features":["Automatic focusing on videos with multiple speakers","Option to resize video clips into different formats (portrait, landscape, square)","Ability to add subtitles and soundtracks with a click"]}', 'Clipwing is an AI-powered video processing tool that simplifies the process of creating short, engaging video clips from longer videos. It offers features like automatic focusing, resizing options, subtitle and soundtrack addition, and unlimited clip creation. Suitable for various video types, Clipwing enhances usability for social media platforms by allowing landscape to vertical video transformation. It offers a free plan and subscription options with enhanced features.', NULL, '["AI-powered","Content Creation","Social Media"]');
