INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8aa9020d-0065-4098-97bf-1d3e68a49dbb', '2024-04-12T15:28:12.604Z', 'Clothoff', 'https://clothoff.io/en', 'images/clothoff.webp', 'images/clothoff-icon.webp', '{"what":"Clothoff is an AI tool that claims to undress individuals in photos using deepnude AI technology. Users can upload a photo and receive the undressed result after a few seconds. The website emphasizes that the service is free to use and highlights privacy measures.","who":"This product is for individuals who are interested in AI technology and image editing. It may appeal to those looking for a unique and controversial tool for photo manipulation.","best_features":["Advanced undressing AI technology","Free to use service","Privacy-focused approach"]}', 'Clothoff is an AI tool that uses deepnude AI technology to undress individuals in photos. Users can upload a photo and receive the undressed result for free. The website emphasizes privacy and states that no data is saved. Users are responsible for the images they generate.', NULL, '["Image Editing"]');
