INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1dfa3031-0f0c-483b-a53d-a380e6d43c89', '2024-04-12T15:28:12.613Z', 'CoffeeChat AI', 'https://www.coffeechatai.com/', 'images/coffeechat-ai.webp', 'images/coffeechat-ai-icon.webp', '{"what":"CoffeeChat AI is an intelligent tool designed to enhance coffee chat experiences by generating tailored questions based on a person''s bio and selected vibe.","who":"CoffeeChat AI is for individuals looking to have productive and meaningful conversations in professional networking engagements, podcast interviews, or any scenario where insightful questions are valuable.","best_features":["Tailored question generation based on user input","Ability to select professional or casual vibe for the engagement","AI-powered tool that improves question generation over time"],"pricing":[]}', 'CoffeeChat AI is an AI-powered tool that generates insightful questions for coffee chats based on the person''s bio and selected vibe. It is designed to enhance engagement in professional networking, podcast interviews, and other scenarios where meaningful conversations are important.', NULL, '["Professional Networking","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3f13bc94-3a22-47dc-bd4a-0d667fdc93fe', '2024-04-12T15:28:12.613Z', 'Coflow', 'https://www.joincoflow.com/', 'images/coflow.webp', 'images/coflow-icon.webp', '{"what":"Coflow is an AI tool that enables users to build custom AI workflows without the need for coding. It provides a user-friendly solution for automating tasks through AI workflows, eliminating the need for coding and streamlining productivity.","who":"Coflow is designed for individuals and businesses looking to automate various tasks such as email follow-ups, sales reports, and document analysis. It is ideal for users who want to create AI solutions without the requirement of coding skills.","best_features":["Drag and drop interface for easy creation of AI workflows","Connectivity to existing platforms for data processing and action-taking by the AI","Ability to extract information from text, websites, and PDFs for storage in databases"]}', 'Coflow is an AI tool that enables users to build custom AI workflows without coding. It offers a drag and drop interface for easy creation of AI solutions that automate tasks like email follow-ups, sales reports, and document analysis. Users can connect to existing platforms for data processing and action-taking by the AI, reducing manual tasks and saving time. Coflow also allows extraction of information from text, websites, and PDFs for storage in databases. Built using Tailwind CSS and the Flowbite component library, Coflow provides flexibility and customization options for creating landing pages.', NULL, '["Automation","No-code Development","Productivity Tools"]');
