INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3e4ce45a-530e-4672-b31f-20de84e2313e', '2024-04-12T15:28:12.659Z', 'Cypher', 'https://cypher.is/', 'images/cypher.webp', 'images/cypher-icon.webp', '{"what":"Cypher is an innovative AI tool developed by Corso Technologies that creates an AI version of the user, simulating and replicating their behavioral patterns and responses.","who":"Cypher is for individuals looking to gain insights into their personality and behavioral patterns through AI algorithms. It is also suitable for active communities, celebrities, and influencers seeking to engage with their audience through AI characters.","best_features":["Creates an AI version of the user to provide insights into their personality and behavioral patterns.","Utilizes cutting-edge AI technologies for effective analysis and processing of user data.","Offers a user-friendly interface prioritizing privacy and confidentiality of user data."]}', 'Cypher is an AI tool that creates a personalized AI version of the user, providing insights into their personality and behavioral patterns. It uses advanced AI algorithms to simulate and replicate the user''s responses based on past interactions. The tool is designed to offer a new perspective on self-analysis and understanding one''s own behavioral patterns.', NULL, '["Personalization","Behavioral Analysis","Privacy"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('31f066d1-a1ce-4609-828d-0813bb74094b', '2024-04-12T15:28:12.659Z', 'Cyphertech', 'https://askcyph.cypherchat.app/', 'images/cyphertech.webp', 'images/cyphertech-icon.webp', '{"what":"AskCyph™ LITE is a lightweight AI chatbot developed by Cypher Tech Inc. that allows users to run an AI model directly in their web browser. With a focus on privacy and security, AskCyph™ LITE enables users to access AI capabilities without the need for sending data over the internet.","who":"AskCyph™ LITE is for individuals and businesses looking for a privacy-focused AI chatbot solution that operates locally on the user''s device, providing control over data and moderation.","best_features":["Privacy-focused AI chatbot that runs locally on the user''s device","Two Lite Models: BASIC and ADVANCED with different knowledge domains","User control over moderation levels"],"pricing":[]}', 'AskCyph™ LITE is a lightweight AI chatbot developed by Cypher Tech Inc. that enables users to run an AI model locally on their device, ensuring privacy and security. The tool offers two Lite Models: BASIC and ADVANCED, each with its own system requirements. Users have control over moderation levels and can access AI capabilities without sending data over the internet. While the tool may occasionally generate incorrect responses, the development team is working towards improvements.', NULL, '["Chat Solutions","Privacy-Focused","Local AI Model"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('83cd0ed3-0d82-4c52-abe1-42ba91f65e45', '2024-04-12T15:28:12.660Z', 'D&AVIZ', 'https://dnaviz.aipeople.arivu-iq.com/', 'images/d&aviz.webp', 'images/daviz-icon.svg', '{"what":"D&AVIZ is an AI-powered data visualization tool that converts raw data into interactive and easy-to-understand visuals, aiding in making informed decisions through real-time performance monitoring and customizable visualizations.","who":"D&AVIZ is designed for data analysts, business professionals, researchers, and anyone who works with data and seeks to gain insights and make data-driven decisions.","best_features":["AI-powered data visualization for converting raw data into interactive visuals","Real-time data visualization for accurate performance monitoring","Customizable visualizations and interactive dashboards for personalized insights"]}', 'D&AVIZ is an AI-powered data visualization tool that transforms raw data into interactive visuals like graphs and charts. It offers real-time performance monitoring, customizable visualizations, and the ability to share interactive dashboards. Users can import data from various sources and customize their visualizations to match their specific needs. The tool enhances decision-making by identifying patterns, trends, and insights within the data.', NULL, '["AI Tools","Software Development","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f6ea791c-1e3b-4b3b-814a-765da09154f9', '2024-04-12T15:28:12.660Z', 'D&D Character Portraits', 'https://www.dndcharacterportraits.com/', 'images/d&d-character-portraits.webp', 'images/d&d-character-portraits-icon.webp', '{"what":"D&D Character Portraits is a tool that allows users to create AI-generated portraits of their Dungeons and Dragons (D&D) characters in their own likeness.","who":"This product is for Dungeons and Dragons (D&D) players who want to create personalized portraits of their characters based on their own likeness.","best_features":["Cutting edge AI technology for high-quality portraits","Custom portraits based on user-provided images","Privacy-focused service with data deletion after 30 days"],"pricing":[{"plan":"Single Project","price":9.99,"billing_cycle":"One-time payment","original_price":9.99,"discount":"","features":["Generate 30 custom D&D portraits","Additional 15 portraits available for $3.99","Fast processing time of around 30 minutes"]}]}', 'D&D Character Portraits is a tool that uses cutting edge AI technology to create personalized portraits of Dungeons and Dragons (D&D) characters based on user-provided images. Users can upload 15 high-resolution images of themselves, specify their character''s race and class, and receive 30 unique portraits in less than an hour. The service prioritizes data privacy by deleting all original images and AI models after portrait generation, as well as deleting the portraits after 30 days. The tool supports PNG, JPG, and HEIC image formats and uses secure payment processing through Stripe.', NULL, '["AI Tools","Gaming","Entertainment"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d8bc8c58-20b6-43a5-b5ae-289f2a907d7d', '2024-04-12T15:28:12.660Z', 'Daily Portfolio Updates', 'https://www.dailyportfolioupdates.com/', 'images/daily-portfolio-updates.webp', 'images/daily-portfolio-updates-icon.webp', '{"what":"The ''Daily Portfolio Updates'' tool is an AI system designed to provide users with regular over-the-phone updates about their portfolio. The tool uses artificial intelligence to analyze and digest the user''s portfolio, generating daily reports.","who":"This tool is for individuals who are looking to stay informed about their investments and make more informed decisions regarding their portfolio.","best_features":["AI-powered daily portfolio updates delivered over the phone","Comprehensive breakdown of the user''s portfolio to enhance understanding","Personalized and intelligent insights to potentially improve investment strategies"],"pricing":[]}', 'The ''Daily Portfolio Updates'' tool is an AI system that delivers daily over-the-phone updates about the user''s portfolio. It provides comprehensive breakdowns of the portfolio to help users understand their investments better and offers personalized insights to potentially enhance their investment strategies. Users receive a three-minute update daily, making it easy to stay informed and make informed decisions.', NULL, '["AI Solutions","Investment Tools"]');
