INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('baec49d5-c073-45c8-98ca-19fb9b8523f1', '2024-04-12T15:28:12.661Z', 'Dankgpt', 'https://www.dankgpt.com/', 'images/dankgpt.webp', 'images/dankgpt-icon.webp', '{"what":"DankGPT is a research tool powered by GPT4, designed for students and academics to analyze complex content found in multiple documents.","who":"This tool is for students and academics who need instant insights and assistance in research and analysis.","best_features":["Instant insights and analysis of complex content","Powerful prompting methods like AMA and chain-of-thought prompting","User-friendly interface with free trial option"],"pricing":[]}', 'DankGPT is a research tool specifically designed for students and academics, powered by GPT4. It provides instant insights and aids in the breakdown and analysis of complex content found in multiple documents. The tool offers various prompting methods to assist users in their research endeavors.', NULL, '["AI Assistance","Education"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d35bc1d3-cbc7-44e4-90cd-c795cb01ecd3', '2024-04-12T15:28:12.661Z', 'Dante AI', 'https://dante-ai.com/', 'images/dante-ai.webp', 'images/dante-ai-icon.webp', '{"what":"Dante AI is an AI tool that allows users to create personalized AI chatbots for their business or personal use. It offers customization options, sentiment analysis, and easy integration with websites and other platforms.","who":"Dante AI is designed for non-technical users who want to create customized AI chatbots for their businesses or personal use. It is suitable for individuals, small businesses, and organizations looking to enhance their online presence and customer engagement.","best_features":["Customizable look and feel of the chatbot, including logos, colors, fonts, and styling.","Powered by GPT-4, OpenAI''s advanced Language Model, enabling detection of over 100 languages.","No coding required to train the AI chatbot on user data and share it on websites or integrate with other platforms."],"pricing":[]}', 'Dante AI is an AI tool that enables users to create personalized AI chatbots for their business or personal use. It offers customization options, sentiment analysis, and easy integration with websites and other platforms. The chatbot is powered by GPT-4, making it language-friendly and inclusive. Dante AI is user-friendly, requiring no coding experience, and offers a free live demo and trial version. It is built on Amazon Web Services with strong encryption for data protection.', NULL, '["Chat Solutions","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6f1ec63c-9bee-44c4-aab7-5740ceb6e52d', '2024-04-12T15:28:12.662Z', 'DappSlap', 'https://www.dappslap.ai/', 'images/dappslap.webp', 'images/dappslap-icon.webp', '{"what":"DappSlap is an AI-powered tool designed to expedite the creation of Web3 decentralized applications (dApps) by transforming Web3 development based on user prompts.","who":"This tool is for developers, hobbyists, and entrepreneurs looking to quickly generate a wide variety of dApps without the need for traditional development practices.","best_features":["Prompt-based AI-powered dApp generation model for quick development of various web applications.","Easy access to creation and provider accounts for effective management of software applications.","Focus on lessening complexity and technical barriers while maintaining quality and functionality of resulting dApps."],"pricing":[]}', 'DappSlap is a tool that leverages advanced AI technology to streamline the creation of Web3 decentralized applications. By using user prompts, this tool enables the instant generation of dApps without the complexities of traditional development practices. It aims to foster creativity and efficiency within the Web3 development sector by providing a prompt-based AI-powered dApp generation model. With easy access to creation and provider accounts, users can effectively manage their software applications. DappSlap focuses on reducing technical barriers and complexity associated with dApp development while ensuring the quality and functionality of the resulting decentralized applications.', NULL, '["Web Development","Artificial Intelligence","Developer Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e16a6219-7539-4ba2-b675-45aabf5d5743', '2024-04-12T15:28:12.662Z', 'Dash AI', 'https://www.dashworks.ai/', 'images/dash-ai.webp', 'images/dash-ai-icon.webp', '{"what":"Dash AI is an Enterprise AI Knowledge Assistant developed by Dashworks, designed to expedite the discovery of internal company information using AI-powered search functionality.","who":"Dash AI is particularly useful for areas like engineering, sales, marketing, and customer support within a company.","best_features":["Generative AI for accurate answers with citations","Comprehensive integrations with various company apps","Secure and private design compliant with SOC 2 Type II, HIPAA, and GDPR"],"pricing":[{"plan":"Standard Plan","price":100,"billing_cycle":"per user per month","original_price":150,"discount":"30% off","features":["Generative AI answers","Comprehensive integrations","Secure and private design"]},{"plan":"Enterprise Plan","price":200,"billing_cycle":"per user per month","original_price":250,"discount":"20% off","features":["Generative AI answers","Comprehensive integrations","Secure and private design"]}]}', 'Dash AI is an Enterprise AI Knowledge Assistant developed by Dashworks, offering AI-powered search functionality to expedite internal company information discovery. It provides accurate answers sourced from connected apps, improves with human feedback, and operates across various areas like code, CRM data, emails, and more. Dash AI integrates smoothly with applications like HubSpot, Salesforce, and Slack, enhancing institutional knowledge management and team performance.', NULL, '["Enterprise","Knowledge Management","Team Performance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d5388e87-afb6-4604-b80c-ef14c1c0b3db', '2024-04-12T15:28:12.662Z', 'Dasha', 'https://dasha.ai/en-us', 'images/dasha.webp', 'images/dasha-icon', '{"what":"Dasha is a conversational AI-as-a-service platform that enables developers to create realistic voice and text conversations for apps and products using Dashascript programming language.","who":"Dasha is designed for developers looking to integrate conversational capabilities into their applications for various industries such as call centers, lead generation, insurance, and more.","best_features":["Declarative programming language, Dashascript, for designing complex conversations","Integration with existing infrastructure and services for valuable insights and analytics","Free tier for testing and building conversational apps"],"pricing":[]}', 'Dasha is a conversational AI-as-a-service platform that allows developers to create realistic voice and text conversations for apps and products. It uses a declarative programming language, Dashascript, to design complex real-world conversations. Dasha integrates with existing infrastructure and services, including databases and external services, to provide valuable insights and analytics. It also offers a free tier for testing and building conversational apps. The platform can be used for call centers, lead generation, insurance, and more.', NULL, '["Developer Tools","AI Solutions","CRM"]');
