INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c842647e-3b37-48af-b9ae-ef2693d50d3f', '2024-04-12T15:28:12.667Z', 'DaVinciFace', 'https://www.davinciface.com/', 'images/davinciface.webp', 'images/davinciface-icon.webp', '{"what":"DaVinciFace is an AI tool that uses deep learning to generate Leonardo da Vinci-style portrait photos of human faces in less than 2 minutes.","who":"This product is for individuals who are interested in transforming their photos into classic DaVinci-style portraits.","best_features":["Generative neural network (GAN) technology for accurate portrait generation","More than 500 million parameters for training the network for high-quality results","Quick turnaround time of less than 2 minutes for generating portraits"],"pricing":[]}', 'DaVinciFace is an AI tool that creates DaVinci-style portrait photos of human faces using deep learning technology. It offers a quick and easy way to transform your photos into classic masterpieces in less than 2 minutes.', NULL, '["AI Tools","Image Editing","AI Services"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('36ff0052-00b8-4639-bafc-aae5d4fa78bd', '2024-04-12T15:28:12.667Z', 'Davis Stirling', 'https://davis-stirling.ai/', 'https://media.theresanaiforthat.com/davis-stirling.png', 'images/davis-stirling-icon.webp', '{"what":"The Davis-Stirling ChatGPT is an AI tool designed to help homeowners and HOA boards manage their property more efficiently by providing insightful and objective responses to queries related to HOA management.","who":"This product is for homeowners and HOA boards looking to navigate complex legislation surrounding HOA management and stay compliant with regulations.","best_features":["Cutting-edge AI technology powered by ChatGPT for accurate and timely responses to user queries.","Seamless and intuitive user experience for streamlined decision-making processes.","Empowers users to rethink how they manage their properties in the ever-changing landscape of HOA management."],"pricing":""}', 'The Davis-Stirling ChatGPT is an AI tool that leverages cutting-edge technology to help homeowners and HOA boards navigate complex legislation related to HOA management. By providing insightful and objective responses to user queries, this tool ensures compliance with local laws and regulations. With a user-friendly interface, it streamlines the decision-making process and empowers users to manage their properties effectively.', NULL, '["Real Estate","Legal Assistance"]');
