INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('db126dcf-1143-4515-829a-463cb1981234', '2024-04-12T15:28:12.667Z', 'Daydream', 'https://daydream.co/', 'images/daydream.webp', 'images/daydream-icon.webp', '{"what":"Daydream is an AI-powered analytics, insights, and reporting tool designed for C-level executives, COOs, finance teams, and operations teams. It connects hard data with the ''story'' behind it, enabling better decision-making and rapid execution in operational workflows.","who":"Daydream is for CEOs, Executives, Founders, CROs, GTM Leadership, BizOps, RevOps, FP&A teams, Data Teams, and anyone who wants to understand their business at a deeper level and improve performance.","best_features":["Connects data with written context and team collaboration","Provides comprehensive metrics and reporting for various roles","Offers templates for quick setup and access to data via non-SQL methods"],"pricing":[]}', 'Daydream is an AI-powered analytics, insights, and reporting tool that connects data with the ''story'' behind it, enabling better decision-making and rapid execution in operational workflows. It provides collaborative features, comprehensive metrics, and templates for quick setup, making it ideal for C-level executives, finance teams, and operations teams.', NULL, '["Data Analysis","Reporting","Collaboration"]');
