INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a181f18c-499b-4ccc-bf17-185769673a62', '2024-04-12T15:28:12.668Z', 'DealGetter9000', 'https://dealgetter9000.com/', 'images/dealgetter9000.webp', 'images/dealgetter9000-icon.webp', '{"what":"DealGetter9000 is an AI-powered chatbot designed to provide valuable support to sales representatives in their pursuit of closing deals. It assists salespeople in navigating the complexities of sales coaching by interpreting sales coaching inputs and understanding the specific needs and pain points of sales representatives.","who":"DealGetter9000 is for sales representatives who want to effectively diagnose their deals, collaborate with their managers or work independently, and receive insights and assistance to successfully close deals, overcome competition, address pain points, and engage the right stakeholders.","best_features":["Interprets sales coaching inputs to understand specific needs and pain points of sales reps.","Accurately determines if pain associated with a deal has been explicitly stated or assumed.","Analyzes if competing solutions have been mentioned and if the next step in the deal is scheduled on the prospect''s calendar."],"pricing":[]}', 'DealGetter9000 is an AI-powered chatbot that supports sales representatives in closing deals successfully by providing insights and assistance based on evaluations of active deals. It helps sales reps overcome challenges, diagnose deals effectively, and engage the right stakeholders. The chatbot can interpret sales coaching inputs, determine the status of pain points in deals, identify competing solutions, and analyze the next steps in the sales process.', NULL, '["AI Agents","Chat Solutions","Coaching"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0c07904c-3206-4ed5-880d-60ff5d13f9cf', '2024-04-12T15:28:12.668Z', 'Dealight', 'https://www.dealight.ai/', 'images/dealight.webp', 'images/dealight-icon.webp', '{"what":"Dealight is an AI-powered tool designed to facilitate the process of pitching business ideas by aligning pitches with relevant investors.","who":"Dealight is for early stage founders looking to refine their pitch decks and connect with interested investors.","best_features":["Real-time insights for refining pitch decks based on AI analysis","Matchmaking with investors based on pitch deck scores","Detailed suggestions, market statistics, and mock interview questions provided"]}', 'Dealight is an AI-driven platform that provides real-time insights to refine business pitches and connect early stage founders with interested investors. By analyzing pitch decks and providing feedback, Dealight helps founders improve their pitches and increase their chances of successful matchmaking with investors. The tool utilizes AI to simulate potential investor questions, identify market statistics, and suggest improvements for pitch decks. Dealight also offers different pricing plans with features such as pitch deck reviews, market research reports, and mock interview questions.', NULL, '["Pitch Deck Analysis","Investor Matchmaking","Business Refinement"]');
