INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('15abf3b0-5ef7-4b2b-b642-59a9c8755fde', '2024-04-12T15:28:12.669Z', 'Debaters', 'https://www.debaters.ai/', 'images/debaters.webp', 'images/debaters-icon.webp', '{"what":"Debaters.ai is an AI tool designed to facilitate structured debates on various topics by analyzing arguments and generating concise summaries.","who":"Debaters.ai is for individuals or teams who engage in debates and discussions on different topics and want to efficiently extract key insights and perspectives.","best_features":["Advanced natural language processing algorithms for accurate analysis of arguments","Objective and balanced summaries of debates for comprehensive understanding","Time-saving tool for dissecting complex debates and forming informed conclusions"],"pricing":""}', 'Debaters.ai is an AI tool that assists in structured debates by analyzing arguments and providing concise summaries. It captures contrasting perspectives, such as the debate on remote work versus office work, and synthesizes key points made by different debaters. By utilizing advanced natural language processing algorithms, Debaters.ai offers objective and insightful analyses, enabling users to evaluate viewpoints and make informed decisions. It is a valuable tool for cataloguing and summarizing debates, allowing users to explore diverse perspectives on various topics.', NULL, '["Debate Facilitation","Argument Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('24864bc7-c261-42a9-9896-b8759f336cd4', '2024-04-12T15:28:12.669Z', 'Debatia', 'https://www.debatia.ai/en', 'images/debatia.webp', 'images/debatia-icon', '{"what":"Debatia is a global AI-judged debate platform that allows users to engage in real-time debates through voice and text input options. It offers multilingual support, skill-based matchmaking, and an AI Judging System powered by ChatGPT.","who":"Debatia is designed for individuals who enjoy debating on a variety of topics and want to engage with debaters worldwide. It is suitable for students, professionals, and anyone interested in honing their debating skills in a fair and interactive environment.","best_features":["Multilingual support for debates in 12 languages, promoting global connectivity and breaking geographical barriers.","Skill-based matchmaking algorithm that pairs users based on their proficiency in debating, ensuring challenging and engaging debates.","AI Judging System powered by ChatGPT, providing unbiased judgment and enhancing the overall debating experience."]}', 'Debatia is a global AI-judged debate platform that facilitates real-time debates in multiple languages. It offers skill-based matchmaking and an AI Judging System powered by ChatGPT, ensuring fair and engaging debates on a wide range of topics.', NULL, '["AI Solutions","Social Connection"]');
