INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c667a4ee-9361-4d26-840b-380fcc6a550d', '2024-04-12T15:28:12.672Z', 'Decoratly', 'https://www.decoratly.com/', 'images/decoratly.webp', 'images/decoratly-icon.webp', '{"what":"Decoratly is an AI-powered tool that enables users to transform interior and exterior spaces into different styles by uploading pictures and specifying style preferences.","who":"Decoratly is designed for both professionals and individuals who desire a user-friendly and flexible design platform for their home or exterior spaces.","best_features":["AI technology for realistic and impressive design alterations based on user preferences.","Two rendering modes - ''makeover'' for radical transformations and ''enhance'' for subtle changes.","27 different style options for users to experiment with various design ideas."],"pricing":[]}', 'Decoratly is an AI-powered tool that allows users to seamlessly transform interior and exterior spaces into different styles. Users can upload pictures, specify style preferences, and generate realistic design alterations. With two rendering modes and 27 style options, Decoratly offers flexibility and personalization in the design process.', NULL, '["AI Tools","Design","Artificial Intelligence"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('989fed56-ef72-4274-927f-77cf0ad65896', '2024-04-12T15:28:12.672Z', 'Decorify', 'https://decorify.app/', 'images/decorify.webp', 'images/decorify-icon.webp', '{"what":"Decorify.app is a user-friendly tool that allows users to effortlessly transform their living spaces with stunning interior design. It features advanced AI technology for instant room redesigns and offers various design themes for customization.","who":"Decorify.app is for anyone looking to enhance their interior design skills, whether they are professional designers or individuals seeking to personalize their living spaces with ease.","best_features":["Advanced AI technology for instant room redesigns in over eight different themes","User-friendly interface for easy visualization and experimentation with design options","Supported by reputable company Advanced Data Innovation Limited for a trustworthy user experience"],"pricing":[]}', 'Decorify.app is a tool that simplifies interior design by allowing users to create beautiful rooms in any style effortlessly. With its advanced AI technology, users can redesign rooms in various themes by simply taking a picture. The app is intuitive, efficient, and supported by a reputable company, making it a valuable tool for anyone looking to elevate their interior design skills.', NULL, '["Interior Design","AI Technology","Visualization"]');
