INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('15d160cd-4d15-4d6d-a941-8e550d472396', '2024-04-12T15:28:12.675Z', 'DeepSwapper AI', 'https://www.deepswapper.com/', 'images/deepswapper-ai.webp', 'images/deepswapper-ai-icon.svg', '{"what":"DeepSwapper AI is a tool that leverages artificial intelligence technology to enable face swapping in images, providing users with a fun and realistic experience.","who":"DeepSwapper AI is for individuals who enjoy creating entertaining and creative content through face swapping in images.","best_features":["No Watermark: Preserve the original aesthetics of images without any watermarks.","Unlimited Swaps: Swap faces in images without any limitations.","High Quality: Generate realistic face swaps for a believable outcome."],"pricing":[]}', 'DeepSwapper AI is a free and unlimited AI face swap tool that allows users to switch faces between two photos effortlessly. The tool focuses on providing high-quality and realistic face swaps without watermarks, ads, or limitations. Users can enjoy a seamless experience and explore various creative possibilities with face swapping.', NULL, '["AI Tools","Entertainment"]');
