INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('38761970-f523-4079-be5a-43eeb0e23f31', '2024-04-12T15:28:12.682Z', 'Dewatermark AI', 'https://dewatermark.ai', 'images/dewatermark-ai.webp', 'images/dewatermark-ai-icon.webp', '{"what":"Dewatermark AI is an online tool that utilizes AI to detect and remove watermarks from images efficiently.","who":"This product is for individuals and businesses looking to easily remove watermarks from their images without compromising quality.","best_features":["AI-powered watermark detection and removal for efficient processing","Manual brush feature for thorough removal of complex watermarks","Maintains image quality and allows for image upscaling"],"pricing":""}', 'Remove Watermark AI is an online tool that utilizes AI to detect and remove watermarks from images efficiently. Users can easily upload their watermarked images, and the AI processes them quickly, automatically detecting and removing the watermarks. For more intricate cases, a manual brush feature is available to ensure thorough removal of any remaining traces. The tool maintains image quality throughout the process, even allowing users to upscale their images if needed. Compatible with various devices, including iOS and Android, this tool provides a fast and easy solution for enhancing, retouching, resizing, and downloading photos with lightning speed.', NULL, '["AI Tools","Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7d6b95b5-95b7-4bd3-9b9a-62a8e95ca0f9', '2024-04-12T15:28:12.682Z', 'Dewatermark', 'https://dewatermark.ai/', 'images/dewatermark.webp', 'images/dewatermark-icon.webp', '{"what":"Dewatermark.AI is an AI-powered tool designed to automatically detect and remove watermarks from images, offering both automatic and manual removal options.","who":"This product is for individuals and businesses who need to quickly and efficiently remove watermarks from images without compromising quality.","best_features":["Utilizes AI technology for accurate and quick watermark removal","Retains original image quality with no loss or fading of details","Offers both automatic and manual removal options for flexibility"],"pricing":""}', 'Dewatermark.AI is a free, user-friendly tool that uses AI technology to detect and remove watermarks from images on various platforms. It ensures no quality loss and offers both automatic and manual removal options for complex watermarks. Users can choose between the original and enhanced versions of their images after removal.', NULL, '["AI Tools","Software Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d605479b-a6d3-4966-88b9-02deb4230977', '2024-04-12T15:28:12.682Z', 'Dewey', 'https://www.withdewey.com/', 'images/dewey.webp', 'images/dewey-icon.webp', '{"what":"Dewey is an AI tool designed to help users manage their to-do lists and develop new habits through personalized SMS text message reminders. It can provide simple answers to questions and offer tailored nudges throughout the day to keep users on track. Dewey offers a free plan for up to three open tasks and a paid ''best friend'' plan with unlimited tasks and additional perks.","who":"Dewey is ideal for individuals who struggle with staying organized, managing their time effectively, and forming new habits. It is suitable for busy professionals, students, entrepreneurs, and anyone looking to improve their productivity and daily routines.","best_features":["Personalized SMS reminders for tasks and habit formation","Ability to answer simple questions and provide customized nudges","Free plan with up to three open tasks and a paid ''best friend'' plan for unlimited tasks and perks"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Up to three open tasks","Personalized SMS reminders","Basic habit formation support"]},{"plan":"Best Friend Plan","price":9.99,"billing_cycle":"Monthly","original_price":9.99,"discount":"N/A","features":["Unlimited tasks","Advanced habit formation support","Additional perks and customization options"]}]}', 'Dewey is an AI tool that leverages SMS text messages to assist users in managing their to-do lists and fostering new habits. With customizable reminders and personalized nudges, Dewey aims to enhance productivity and organization for individuals across various domains. The free plan allows users to experience the benefits of Dewey with limited tasks, while the paid ''best friend'' plan offers unlimited task management and exclusive features.', NULL, '["AI Assistance","Task Management","Habit Formation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1c7ad666-9956-42b7-a26c-0c6242822356', '2024-04-12T15:28:12.683Z', 'DEX Insight', 'https://www.dexinsight.com/', 'images/dex-insight.webp', 'images/dex-insight-icon.webp', '{"what":"DEX Insight is a tool designed to enhance the employee experience with digital tools by gathering application usage data and obtaining user feedback.","who":"DEX Insight is for businesses looking to optimize their technology and user efficiency, especially in high-compliance sectors.","best_features":["Measure Monthly Active Users of any licensed or internal app","Obtain in-app feedback for enhancing high-traffic workflows","Transform insights into action at an impressive speed"],"pricing":[{"plan":"Enterprise Plan","price":50000,"billing_cycle":"annually","original_price":50000,"discount":"","features":["Track MAUs and gather feedback from desktop apps","Available in 27 languages","Low 5-figures annual cost with value scaling as end-users increase"]}]}', 'DEX Insight is a browser extension and desktop agent tool that provides bespoke feedback integration to understand and enhance the employee experience with digital tools. It offers actionable insights based on user feedback, allowing firms to align their technology and user efficiency. The tool goes beyond simple analytics by measuring Monthly Active Users, providing granular analytics tailored for high-stakes environments, and ensuring every tool is optimized for peak performance.', NULL, '["Data Analysis","Technology","User Experience"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1c31c9c2-ec6f-4608-a019-17783da3b2c7', '2024-04-12T15:28:12.683Z', 'Dexa', 'https://dexa.ai/', 'images/dexa.webp', 'images/dexa-icon.webp', '{"what":"Dexa is an AI-powered tool that assists users in exploring, searching, and asking questions about their favorite podcasts.","who":"Dexa is for podcast enthusiasts who want to delve deeper into their favorite shows and gain insights from trusted experts.","best_features":["AI assistants tailored to answer user questions sourced directly from podcast episodes","Intuitive podcast search feature for easy episode discovery","Early access to popular shows and personalized AI interactions"],"pricing":""}', 'Dexa is a revolutionary AI-powered tool that enhances the podcast listening experience by providing personalized AI assistants to answer user questions sourced from podcast episodes. Users can easily search for relevant episodes, access popular shows, and interact with AI bots to gain insights from trusted experts. Dexa offers a new level of podcast interaction, enabling users to explore the podcast universe like never before.', NULL, '["AI Assistance","Personalized Experience","Podcast Discovery"]');
