INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c00bff11-5835-459c-bc02-9820dc44b776', '2024-04-12T15:28:12.710Z', 'DrugCard', 'https://drug-card.io/', 'images/drugcard.webp', 'images/drugcard-icon.svg', '{"what":"DrugCard is an AI-enabled tool designed for local literature screening in the pharmaceutical industry. It streamlines drug safety routines and ensures compliance with regulatory requirements.","who":"DrugCard is designed for Contract Research Organizations (CROs), Marketing Authorization Holders (MAHs), and freelancers within the pharmaceutical industry.","best_features":["Continuous and regular screening to stay up-to-date with the latest literature in local markets.","Traceable and transparent screening process for accuracy and completeness.","Scalable to new markets with support for multiple languages and suppliers."],"pricing":[]}', 'DrugCard is an AI-enabled tool for local literature screening in the pharmaceutical industry. It offers continuous and regular screening, traceability, transparency, scalability, and support for multiple languages and suppliers. The platform is cost-effective, accurate, and efficient, catering to CROs, MAHs, and freelancers.', NULL, '["Pharmacovigilance","Drug Safety","Literature Screening"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6721ffc6-1304-468f-9046-6150643ac9b9', '2024-04-12T15:28:12.710Z', 'Drumless', 'https://drumless.app/', 'images/drumless.webp', 'images/drumless-icon.webp', '{"what":"Drumless is an AI-powered tool designed to help drummers practice and improve their skills by removing drum sounds from songs, allowing them to play along with their favorite music in a personalized and creative way.","who":"Drumless is aimed at students, teachers, hobbyists, and streamers who are looking to enhance their drumming skills and engage with music in a new and innovative manner.","best_features":["AI-powered drum sound removal","Support for MP3 and WAV files","Cloud storage for processed files"]}', 'Drumless is an AI-powered tool that allows drummers to remove drum sounds from songs, enabling them to practice and play along with their favorite tracks in a personalized and creative manner. It supports MP3 and WAV files, offers cloud storage for processed files, and is designed to redefine traditional practice methods for drummers.', NULL, '["AI Tools","Music Practice","Cloud Storage"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a6709bbb-a023-476f-adf3-8fc20e155b2a', '2024-04-12T15:28:12.710Z', 'Drumloop AI', 'https://drumloopai.com/', 'images/drumloop-ai.webp', 'images/drumloop-ai-icon.webp', '{"what":"Drumloop AI is an innovative tool that assists users in creating captivating drum loops with the power of artificial intelligence. It provides a smart AI technology trained on a vast collection of royalty-free drum beats, functioning as a personal drummer on demand.","who":"Drumloop AI is designed for artists, producers, jammers, and content creators who want to enhance their compositions with custom drum loops, quickly generate drum beats for tracks, enjoy jam sessions with an AI-generated rhythm section, and add dynamic background beats to their content.","best_features":["AI technology trained on royalty-free drum beats","User-friendly interface for easy beat creation","Versatile drum loops that can be customized and integrated into music production software"]}', 'The Drumloop AI tool generates original drum loops with neural audio synthesis. Users can select the desired genre for their beat, and the AI will generate a drum loop that can be adjusted for tempo and downloaded if desired. With over thousands of users and beats generated, the tool showcases its capabilities through sample beats on the website. Users can try the tool themselves to create drum loop samples for inspiration.', NULL, '["AI Tools","Music Production"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a9475ff6-3173-45d7-889f-775b5e312b56', '2024-04-12T15:28:12.711Z', 'Drumloop', 'https://drumloopai.com/', 'images/drumloop.webp', 'images/drumloop-icon.webp', '{"what":"Drumloop AI is an AI-powered tool that generates original drum loops using a neural audio network.","who":"Drumloop AI is designed for artists, producers, jammers, and content creators who want to create unique and inspiring drum loops for their music projects.","best_features":["AI-generated drum loops tailored to user preferences","User-friendly interface for easy navigation and customization","Versatile integration with music production software"],"pricing":{"plan":"Free Trial","price":0,"billing_cycle":"One-time","original_price":0,"discount":"N/A","features":["Create drum loops with AI","Adjust tempo and download loops","Access sample beats for inspiration"]}}', 'Drumloop AI is an innovative tool that uses artificial intelligence to generate original drum loops for music producers and creators. Users can select their desired genre, adjust the tempo, and download the loops to use in their music projects. The tool offers a user-friendly interface and access to sample beats for inspiration. Drumloop AI is perfect for artists, producers, jammers, and content creators looking to enhance their compositions with unique drum loops.', NULL, '["AI Tools","Art & Entertainment"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ebf9a0d3-ee50-40fc-90d3-10c3b6665912', '2024-04-12T15:28:12.711Z', 'DryRun Security', 'https://www.dryrun.security/', 'images/dryrun-security.webp', 'images/dryrun-security-icon.webp', '{"what":"DryRun Security is an AI-powered tool designed to support developers by providing automated in-line security checks during the coding process. It aims to provide a ''security buddy'' in the coding workflow, offering security context and reducing frustrations caused by repetitive alerts or inaccurate results.","who":"DryRun Security is for developers and development teams who want to enhance their coding process by incorporating automated security checks and receiving security context feedback in real-time.","best_features":["Contextual Security Analysis for comprehensive evaluation of code changes","Quick installation as a GitHub App for easy integration","Fast security reviews for code changes to enable faster merging"],"pricing":[]}', 'DryRun Security is an AI-powered tool that provides automated in-line security checks during the coding process. It offers security context feedback in real-time, supports various languages and frameworks, and aims to enhance developer productivity by enabling faster and safer coding practices.', NULL, '["Security","AI-powered","Code Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0648609c-2f09-426c-bfdb-cc07458145e8', '2024-04-12T15:28:12.711Z', 'DSensei', 'https://www.dsensei.app/', 'images/dsensei.webp', 'images/dsensei-icon.webp', '{"what":"DSensei is an open-source AI-powered key driver analysis engine that helps users identify the root cause of fluctuations in their metrics.","who":"DSensei is for data teams and organizations seeking to gain a comprehensive understanding of the factors driving fluctuations in their metrics, enabling data-driven decision-making and strategic insights.","best_features":["Automated root cause analysis saves considerable time for data teams","Identifies the most significant components impacting metrics","Provides intuitive insights into the reasons behind specific movements in datasets"],"pricing":[]}', 'DSensei is an open-source AI-powered key driver analysis engine that focuses on providing insights into the underlying reasons behind changes in metrics. It automates the tedious process of root cause analysis, identifies critical drivers, and uncovers valuable insights that may have otherwise been missed. DSensei enables users to establish a holistic view of the key drivers affecting their metrics and offers a live demo for firsthand experience.', NULL, '["AI Tools","Machine Learning","Artificial Intelligence"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('73cbc44a-0b76-462c-bb53-f38f50ee3f87', '2024-04-12T15:28:12.711Z', 'Dstill podcasts', 'https://dstill.ai/podcasts', 'images/dstill-podcasts.webp', 'images/dstill-podcasts-icon.webp', '{"what":"Dstill podcasts is an AI tool that provides summaries and chat functionality for a wide range of popular podcasts. It aims to save time for users by offering concise summaries and interactive chat capabilities for podcast episodes.","who":"This product is for podcast enthusiasts who want to stay informed about the latest episodes without having to listen to the entire podcast. It is also beneficial for individuals interested in education, entertainment, society, wellness, business, technology, startups, health, or any other subject covered by the featured podcasts.","best_features":["Provides concise summaries of popular podcasts","Offers interactive chat functionality for engaging discussions","Enhances the listening experience by allowing users to ask questions related to the podcast content"],"pricing":""}', 'Dstill podcasts is an AI tool that offers summaries and chat functionality for various popular podcasts. Users can access important information from episodes without listening to the entire podcast. The platform includes podcasts like ''Stuff You Should Know,'' ''The Tim Ferriss Show,'' ''Planet Money,'' and more. Users can engage in chat conversations using ChatGPT, enhancing the listening experience and enabling meaningful discussions around podcast topics.', NULL, '["AI Tools","Content Consumption"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e5d3baee-a3b9-478b-992b-0ba38175c138', '2024-04-12T15:28:12.711Z', 'DubAI', 'https://www.trydub.com/', 'images/dubai.webp', 'images/dubai-icon.webp', '{"what":"Dub AI is a tool designed to aid in the localization of videos using artificial intelligence. It offers translation, voice cloning, and automatic speaker detection services to help users reach a global audience.","who":"Dub AI is for content creators, marketers, and businesses looking to expand their reach by localizing their videos in multiple languages and maintaining brand consistency across diverse markets.","best_features":["Translation of content into 25+ languages for global audience reach","Voice cloning feature to maintain brand identity across markets","Multi-speaker support with automatic speaker detection for up to 10 speakers"]}', 'Dub AI is a user-friendly tool that allows for quick localization of videos through translation, voice cloning, and multi-speaker support. It enables users to reach a global audience by translating content into over 25 languages and maintaining brand identity across diverse markets. With features like automatic speaker detection and access to translated transcripts and audio clips, Dub AI simplifies the localization process in three easy steps.', NULL, '["Translation","Video Editing","AI Solutions"]');
