INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7ab265db-a11a-4708-913c-371a59570450', '2024-04-12T15:28:12.712Z', 'Dubecos', 'https://dubecos.com/', 'images/dubecos.webp', 'images/dubecos-icon.webp', '{"what":"Dubecos is an AI-powered tool designed to provide fast and accurate video dubbing services, breaking language barriers and enabling users to reach global audiences.","who":"Dubecos is for content creators, marketers, and anyone looking to expand their video reach to international audiences by dubbing their content into multiple languages.","best_features":["Effortlessly break language barriers with Ollang AI technology","High-quality dubbing that captures the essence of the original video","Seamlessly dub content into multiple languages to resonate across cultures"],"pricing":[]}', 'Dubecos is an AI-powered tool that revolutionizes video dubbing by leveraging advanced AI technology to seamlessly dub videos into multiple languages. It aims to help content creators expand their reach to global audiences by breaking language barriers and fostering resonance across different cultures.', NULL, '["Video Editing","Translation","Content Creation"]');
