INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6e6546a4-22a3-49de-abce-5d2bf9e849d4', '2024-04-12T15:28:12.763Z', 'Figurative Checker', 'https://figurativechecker.com/', 'images/figurative-checker.webp', 'images/figurative-checker-icon.webp', '{"what":"The Figurative Language Checker is an AI-driven tool designed to identify and analyze figurative language in written content, helping writers enhance their writing skills.","who":"This product is for writers, students, and anyone looking to improve their writing by incorporating figurative language into their content.","best_features":["Quick and accurate identification of similes, metaphors, and personification in written content.","Easy-to-use interface for a hassle-free experience in analyzing text for figurative language.","Provides valuable insights to users, allowing them to refine and enrich their writing effortlessly."],"pricing":[]}', 'The Figurative Language Checker is an AI tool that scans written content to identify and analyze figurative language elements like similes, metaphors, and personification. It helps users enhance their writing by providing valuable insights and improving their writing skills. The tool is designed to be user-friendly, offering a swift and efficient experience for analyzing text for various types of figurative language.', NULL, '["AI Tools","Language Analysis"]');
