INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f298c4e7-74ca-424d-ad81-5038eab2a990', '2024-04-12T15:28:12.778Z', 'Flowpoint.ai', 'https://flowpoint.ai/', 'images/flowpoint-ai.webp', 'images/flowpoint-ai-icon.webp', '{"what":"Flowpoint.ai is a cutting-edge tool powered by artificial intelligence that enhances website conversions and improves return on investment by implementing data-driven solutions.","who":"Flowpoint.ai is designed for businesses looking to maximize their online success by discerning impactful solutions and strategies.","best_features":["Utilizes artificial intelligence to provide data-driven solutions for enhancing website conversions","Helps businesses improve return on investment by implementing impactful strategies","Aids in maximizing online success through data-driven solutions and strategies"],"pricing":[]}', 'Flowpoint is a cutting-edge tool powered by artificial intelligence, designed to enhance website conversions and improve return on investment by implementing data-driven solutions. At its core, it aids businesses in discerning the most impactful solutions and strategies that maximize their online success.', NULL, '["AI Tools","Marketing","Business Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f96d572b-384b-41e1-861f-d99fb585e6fa', '2024-04-12T15:28:12.779Z', 'Flowrite', 'https://www.flowrite.com/', 'images/flowrite.webp', 'images/flowrite-icon.webp', '{"what":"Flowrite is an AI tool designed to assist with email and messaging workflows by generating unique messages based on user instructions. It seamlessly integrates with popular email and messaging tools, offering personalized reply suggestions and time-saving templates.","who":"Flowrite is ideal for individuals and companies looking to streamline their email and messaging communication. It is suitable for professionals in leadership, sales, customer support, HR & recruiting, marketing & communications, and more.","best_features":["Seamless integration with favorite email and messaging tools","Personalized reply suggestions in seconds","Time-saving templates for common messages"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Access to basic features","Limited template options","Limited message generations"]},{"plan":"Pro","price":12,"billing_cycle":"Monthly","original_price":15,"discount":"20% off","features":["Full access to all features","Unlimited template options","Unlimited message generations"]}]}', 'Flowrite is an AI tool designed for frequent email and message workflows. It generates unique messages based on user instructions and covers common messages across roles and teams. It seamlessly integrates with favorite email and messaging tools, providing personalized reply suggestions and time-saving templates.', NULL, '["Email Management","Productivity Tools","AI Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('33e15334-f5fc-479c-bf76-e9a03c9b152a', '2024-04-12T15:28:12.779Z', 'Flowtapes', 'https://flowtapes.com/', 'images/flowtapes.webp', 'images/flowtapes-icon.webp', '{"what":"Flowtapes is a video editing tool designed for digital creatives that automates the process of detecting silences and repetitions in video recordings, allowing users to edit based on text content.","who":"Flowtapes is for content creators, YouTubers, and anyone looking to streamline their video editing process with AI assistance.","best_features":["Automatic video editor that saves time and effort","AI-generated B-Roll clips for added visual appeal","Text-based editor for easy editing based on text content"],"pricing":[{"plan":"Pay-per-usage","price":null,"billing_cycle":null,"original_price":null,"discount":"80% off code for exporting final render","features":["Automatic video editor","AI-generated B-Roll clips","Text-based editor"]}]}', 'Flowtapes is a video editing tool that uses AI algorithms to detect silences and repetitions in video recordings, allowing users to edit their videos based on text content. It offers features like automatic silence removal, AI-generated B-Roll clips, and a text-based editor for efficient editing. Users can upload raw footage, configure automatic edits, preview the results, and export the final edit in high resolution. Flowtapes is a user-friendly tool that simplifies the video editing process for creators.', NULL, '["AI Tools","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('afc7d5f1-121b-4032-a03c-32ebcac9ed3b', '2024-04-12T15:28:12.779Z', 'Fluency.com', 'https://getfluency.com.au/', 'images/fluency-com.webp', 'images/fluency-com-icon.webp', '{"what":"Fluency.com is a process excellence platform designed to streamline business operations and communications by automating the creation of Standard Operating Procedures (SOPs), internal guides, and compliance-related documents.","who":"Fluency.com is ideal for businesses looking to automate their process documentation, improve process oversight, enhance compliance, and save time and resources on manual documentation tasks.","best_features":["Automated generation of process documentation using AI","Centralized storage and organization of processes for effective oversight","Enhanced process security and personalized style guides for exported documents"],"pricing":[]}', 'Fluency.com is a platform that automates the creation of Standard Operating Procedures (SOPs), internal guides, and compliance-related documents, eliminating the need for manual entry. It provides centralized storage and organization for all processes, aiding effective oversight and ensuring the utility of critical information. Fluency.com aims to simplify the audit process, enhance compliance, and save companies considerable time and resources.', NULL, '["Automation","Compliance","Documentation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a575cc71-f690-4531-9836-0f2c5f9074da', '2024-04-12T15:28:12.779Z', 'Fluency', 'https://www.thefluency.app/', 'images/fluency.webp', 'images/fluency-icon.webp', '{"what":"Fluency is an interactive language mastery tool powered by Artificial Intelligence, designed to support a wide range of world languages through interactive and responsive lessons.","who":"Fluency is for both beginners and seasoned language learners who want to develop actual communication skills in various languages, including major Asian and Indian languages.","best_features":["Interactive and responsive lessons for real-world communication skills","Support for over 20 languages, including major Asian and Indian languages","Emphasis on practical interaction and continuous learning"],"pricing":[]}', 'Fluency is an AI-powered language mastery tool that offers interactive lessons in speaking, reading, and writing for learners of various world languages. It focuses on real-world communication skills and provides support for major Asian and Indian languages. The tool promotes practical interaction and continuous learning to enhance language proficiency.', NULL, '["Language Learning","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a6bb12fe-ddbb-49f5-8650-0e96678104b0', '2024-04-12T15:28:12.779Z', 'Fluent', 'https://www.fluent.co/', 'images/fluent.webp', 'images/fluent-icon.webp', '{"what":"Fluent is a browser extension that allows users to effortlessly learn a new language while browsing the web. It offers AI pronunciation lessons, vocabulary learning from online content, and streaks/leaderboards for motivation.","who":"Fluent is ideal for individuals who want to learn a new language but struggle to find dedicated time for study or prefer a more effortless approach to learning. It is suitable for language learners of all levels.","best_features":["AI pronunciation lessons to improve speaking confidence","Vocabulary learning from any online content","Streaks and leaderboards for motivation and tracking progress"],"pricing":[{"plan":"PRO","price":8,"billing_cycle":"monthly","original_price":50,"discount":"Cancel Anytime","features":["AI Pronunciation Lessons","Vocabulary Learning","Streaks and Leaderboards"]}]}', 'Fluent is a browser extension that turns your browser into a language tutor, allowing users to learn a new language effortlessly while browsing the web. It supports multiple languages, offers AI pronunciation lessons, helps users learn vocabulary from online content, and includes streaks and leaderboards for motivation.', NULL, '["Language Learning","Browser Extension"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4a9f6728-58a6-446f-8688-d3e66fe1db09', '2024-04-12T15:28:12.780Z', 'FluentTypeAI', 'https://www.fluenttypeai.com/', 'images/fluenttypeai.webp', 'images/fluenttypeai-icon.webp', '{"what":"FluentTypeAI is an AI-powered tool designed to help users improve their touch typing skills in an engaging and efficient manner.","who":"FluentTypeAI is for individuals looking to enhance their typing speed and accuracy, whether for personal or professional development.","best_features":["AI-Powered Learning: Custom LLMs and Bayesian networks tailor practice text based on user''s typing error patterns.","Comprehensive Statistics: Detailed stats on words-per-minute, accuracy, and frequent mistake patterns for progress tracking.","Competitive Leaderboards: Users can compete with others, climb weekly leaderboards, and track longest streaks and improvements."],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Typing Analysis","Statistics & Progress","LLM Text Generation","Streak Tracking","Custom Prompting"]},{"plan":"Monthly","price":9.99,"billing_cycle":"Monthly","original_price":9.99,"discount":"~2 cups of coffee","features":["Typing Analysis","Statistics & Progress","LLM Text Generation","Streak Tracking","Custom Prompting"]},{"plan":"Annual","price":119,"billing_cycle":"Annual","original_price":119,"discount":"~8 cups of coffee","features":["Typing Analysis","Statistics & Progress","LLM Text Generation","Streak Tracking","Custom Prompting"]}]}', 'FluentTypeAI is an AI-powered tool that revolutionizes touch typing practice by personalizing learning through advanced algorithms and statistical analysis. It offers competitive leaderboards, comprehensive statistics, and a user-friendly interface for efficient typing skill improvement.', NULL, '["AI Tools","Education","Software"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8e8d9be1-613d-4218-a5c2-c079b4d359f1', '2024-04-12T15:28:12.780Z', 'Flush AI', 'https://www.flushai.cloud', 'images/flush-ai.webp', 'images/flush-ai-icon.webp', '{"what":"Flush AI is an AI Art Studio in the cloud that offers customizable multimodal workflows for image enhancement and language tasks.","who":"Flush AI is designed for creators, developers, and businesses looking to enhance images, run language tasks effortlessly, and generate AI-generated content.","best_features":["Ultimate customizability to create multimodal workflows in minutes","Access to a chain of AI image models and generative AI models in one place","Variety of pricing plans catering to different needs"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Experimentation with AI models"]},{"plan":"Subscription Plans","price":"Varies","billing_cycle":"Monthly","original_price":"Varies","discount":"N/A","features":["Larger volumes of image generations and upscales"]},{"plan":"Premium Services","price":"Customized","billing_cycle":"Customized","original_price":"Customized","discount":"N/A","features":["Higher volumes of AI-generated content"]}]}', 'Flush AI is an end-to-end AI art studio in the cloud that offers ultimate customizability to create multimodal workflows in minutes. Users can access a chain of AI image models and generative AI models all in one place, including models like Stable Diffusion XL and RealESRGAN. The tool caters to different needs with a variety of pricing plans, including a free plan for experimentation and subscription plans for larger volumes of image generations and upscales. Premium services are also available for creators and developers requiring higher volumes of AI-generated content.', NULL, '["AI Tools","Image Editing","AI Models"]');
