INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('94e208ad-22a7-4c49-8192-e3c13141c45e', '2024-04-12T15:28:12.780Z', 'Flux', 'https://getflux.io/', 'images/flux.webp', 'images/flux-icon.webp', '{"what":"Flux is an AI-driven tool designed to facilitate effortless note-taking during online meetings and conversations. It goes beyond just transcribing meetings and creating summaries to provide users with a tool that helps them quickly access their past discussions.","who":"Flux is designed for busy teams and individuals who want to streamline their note-taking process during online meetings and have quick access to past discussions.","best_features":["Customizable meeting summaries and templates for different meeting types and audiences.","Integration with popular meeting apps like Google Meet, Zoom, and Microsoft Teams.","Ability to ask questions about past meetings and automate notes and summaries for easy sharing with team members."]}', 'Flux is an AI-driven tool that simplifies note-taking during online meetings by providing customizable meeting summaries, integration with popular meeting apps, and the ability to automate notes and summaries for easy sharing. It also allows users to ask questions about past meetings and access their discussions effortlessly.', NULL, '["AI Solutions","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ddae598a-dc6c-41ab-af73-4110e05e4fbf', '2024-04-12T15:28:12.780Z', 'Fluxguard', 'https://fluxguard.com/', 'images/fluxguard.webp', 'images/fluxguard-icon.webp', '{"what":"Fluxguard is an AI-powered tool designed to detect, filter, and summarize website changes. It accelerates web change monitoring by curating reports ranging from minor alterations to substantial updates.","who":"Fluxguard is designed for businesses and organizations that need to track changes to websites for various purposes such as regulatory compliance, defect detection, competitive insight, and more. It is especially suitable for tracking changes to legislation, regulations, and rules.","best_features":["AI-powered tool for detecting, filtering, and summarizing website changes","Automates evaluation and compliance confirmation to new regulatory policies","Customizable monitoring with specific keywords and pages"]}', 'Fluxguard is an AI-powered tool that detects, filters, and summarizes website changes to accelerate web change monitoring. It is designed to help businesses track changes for regulatory compliance, defect detection, competitive insight, and more. The tool automates evaluation and compliance confirmation to new regulatory policies, offers customizable monitoring options, and integrates seamlessly into workflows with easy-to-read reports.', NULL, '["Regulatory Compliance","Web Monitoring"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('968360c5-50f9-408c-8d01-ade2999676fc', '2024-04-12T15:28:12.780Z', 'Fluxon', 'https://www.fluxon.ai/', 'images/fluxon.webp', 'images/fluxon-icon.webp', '{"what":"Fluxon is an AI tool that specializes in hyper-realistic voice generation, allowing users to transform text into lifelike audio with various voices in any language.","who":"Fluxon is designed for content creators, marketers, audiobook producers, game developers, translators, chatbot developers, and podcasters looking to enhance their audio content with humanlike voices.","best_features":["Hyper-realistic voice generation for lifelike audio","Voice cloning feature for replicating any voice with minimal example audio","Ability to create conversations with multiple voices in the same audio file"],"pricing":[]}', 'Fluxon is an AI tool that specializes in hyper-realistic voice generation, allowing users to transform text into audio with lifelike voices in any language. It offers voice cloning, conversation creation, voice synthesis, and lip-sync video capabilities, catering to various use cases such as marketing videos, audiobooks, gaming, translation, chatbots, and podcasts.', NULL, '["Audio Editing","Translation","Chat Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e077d3dc-128a-4820-bedf-933bd0cfea36', '2024-04-12T15:28:12.781Z', 'Fluxy', 'https://www.fluxyai.com/', 'images/fluxy.webp', 'images/fluxy-icon.webp', '{"what":"Fluxy AI is an AI-powered virtual assistant that helps with various writing tasks, such as assignments, research papers, resumes, cover letters, content creation, translation, and data analysis.","who":"Fluxy AI is designed for individuals and professionals who are looking to enhance their writing productivity with the help of AI. It is suitable for students, writers, job seekers, marketers, sales professionals, and anyone who needs assistance with writing tasks.","best_features":["Over 100 ready-to-use templates categorized under various topics for easy content creation.","Customizable personas, behaviors, and backgrounds to align with unique style and requirements.","Chat GPT feature for lightning-fast responses during customer support."],"pricing":[]}', 'Fluxy AI is an AI-powered virtual assistant that combines human creativity with AI brilliance to enhance productivity in writing tasks. It offers over 100 ready-to-use templates, customizable personas, and a chat GPT feature for quick responses. The tool is suitable for personal and professional writing tasks, such as summarizing, crafting resumes, and creating marketing content. Fluxy AI is available in multiple languages and offers a free trial with no credit card required.', NULL, '["AI Solutions","Content Creation","Translation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('efed8277-304f-481f-be79-ea67eb8e37cc', '2024-04-12T15:28:12.781Z', 'Fly AI', 'https://letsflyai.com/', 'images/fly-ai.webp', 'images/fly-ai-icon.webp', '{"what":"Fly AI is an AI tool designed specifically for macOS users, offering fast and seamless access to OpenAI''s ChatGPT for enhanced workflow efficiency.","who":"Fly AI is ideal for macOS users looking to leverage AI capabilities for improved productivity and workflow optimization.","best_features":["Create custom AI mini-apps for quick access and context-aware responses","Fast and lightweight native macOS app for instant use","Access to latest OpenAI models like ChatGPT and GPT-4 for cutting-edge AI capabilities"],"pricing":[]}', 'Fly AI is a native macOS AI tool that enables users to access OpenAI''s ChatGPT for faster and more efficient workflow. It allows users to create custom AI mini-apps, save frequently used prompts, and access the latest AI models like ChatGPT and GPT-4. Fly AI prioritizes privacy by not recording user data and adhering to OpenAI API policies. It offers a seamless and user-friendly AI experience for macOS users.', NULL, '["Productivity Tools","Software"]');
