INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('039891d4-7e67-4f92-8ac3-67933ea7d77e', '2024-04-12T15:28:12.781Z', 'Flythroughs', 'https://lumalabs.ai/flythroughs', 'images/flythroughs.webp', 'images/flythroughs-icon.webp', '{"what":"Flythroughs by Luma AI is an intuitive app that allows users to create professional-grade Flythroughs using AI technology with just their iPhone.","who":"This product is for individuals and businesses in industries such as real estate, design, architecture, and any other field that requires showcasing spaces in an engaging and immersive way.","best_features":["AI-generated Flythroughs from shaky amateur recorded videos","World''s most advanced 3D Generative NeRF Technology","One-touch generation of photorealistic and lifelike 3D experiences"],"pricing":[]}', 'Flythroughs by Luma AI is an innovative app that enables users to create cinematic 3D Flythroughs without the need for expensive equipment. By utilizing breakthrough NeRF and 3D generative AI technology, users can easily generate professional-grade Flythroughs with just their iPhone. This tool is designed to help users showcase their spaces in a more immersive and engaging way, making it ideal for industries such as real estate, design, and architecture.', NULL, '["Real Estate","Design","Architecture"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fc5b43cd-1440-47a1-b4cb-b609abeef9fc', '2024-04-12T15:28:12.781Z', 'Focal', 'https://www.getfocal.co/', 'images/focal.webp', 'images/focal-icon.webp', '{"what":"Focal is an advanced AI tool designed for readers and researchers to summarize and highlight PDFs and web pages, offering a distraction-free reading experience and AI summaries for academic, legal, and workplace environments.","who":"Focal is for researchers, academics, students, legal professionals, and workplace professionals who need to process vast amounts of information efficiently.","best_features":["AI summaries that distill complex information into comprehendible points","Distraction-free reading experience by eliminating clutter and ads","Secure collaboration with workspaces and projects"],"pricing":""}', 'Focal is an advanced AI tool primarily designed for readers and researchers across various professions. It offers powerful functionalities for summarizing and highlighting PDFs and web pages, making it an ideal solution for those tasked with processing vast amounts of information. Focal stands out for its ''AI Copilot'' feature that allows users to search and query their own data, along with providing a distraction-free reading experience and secure collaboration features.', NULL, '["AI Tools","Productivity Tools"]');
