INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4a6fe7d6-e34c-44eb-a53c-dbe3312db507', '2024-04-12T15:28:12.782Z', 'Focia', 'https://focia.io/', 'images/focia.webp', 'images/focia-icon.webp', '{"what":"Focia is an AI-powered content analysis tool that helps creators and businesses predict engagement metrics before posting content. It allows users to rank and compare content ideas, analyze engagement triggers, receive feedback, and forecast performance across multiple channels with high accuracy.","who":"Focia is designed for content creators, social media managers, digital marketers, and businesses looking to optimize their content strategy and maximize engagement on various platforms.","best_features":["Rank and compare content ideas based on engagement levels","Forecast engagement metrics with high accuracy","Customize insights and predictions based on workspace data"]}', 'Focia is an AI tool that empowers content creators and businesses to make data-driven decisions by predicting engagement metrics, analyzing content performance, and optimizing content ideas for maximum impact. With features like ranking and comparing ideas, forecasting performance, and customizing insights, Focia helps users enhance their content strategy and boost engagement.', NULL, '["Content Analysis","Engagement Prediction","Data-driven Decision Making"]');
