INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cecf09d6-7443-4e50-b406-1d741ecb9327', '2024-04-12T15:28:12.782Z', 'FoldSum', 'https://www.foldsum.com/', 'images/foldsum.webp', 'images/foldsum-icon.webp', '{"what":"FoldSum is a Chrome extension tool that enables text summarization and translation using the ChatGPT model, aimed at enhancing reading efficiency by allowing users to summarize selected text on web pages.","who":"FoldSum is for individuals who want to improve their reading efficiency by quickly summarizing and translating text on web pages.","best_features":["Inline text summarization and translation for improved reading efficiency.","Support for multiple languages in a single step.","Utilizes web-based ChatGPT or POE engine for summarization and translation without extra token cost."],"pricing":[]}', 'FoldSum is a Chrome extension tool that enables users to summarize and translate text on web pages using the ChatGPT model. It helps improve reading efficiency by allowing users to specify text for summarization and translation directly within the webpage. FoldSum supports multiple languages, utilizes a web-based ChatGPT or POE engine, and does not incur any additional token cost. Users can download and install the browser extension to start using FoldSum for free.', NULL, '["AI Tools","Browser Extension"]');
