INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('978e7724-1cdf-4dcf-8ae1-150345896951', '2024-04-12T15:28:12.782Z', 'folk 2.0', 'https://www.folk.app', 'images/folk-2-0.webp', 'images/folk-2-0-icon.webp', '{"what":"folk 2.0 is an all-in-one CRM tool that simplifies contact management and enhances relationship building for individuals and teams.","who":"This product is designed for sales professionals, recruiters, fundraisers, partnership managers, investors, and anyone looking to streamline their contact management and communication processes.","best_features":["Easily import contacts from any website with the folkX Chrome extension","1-click enrichment feature to quickly find contact details like email addresses","AI-powered pipeline management for efficient deal closing and collaboration"],"pricing":[]}', 'folk 2.0 is a comprehensive CRM tool that offers contact management, email campaigns, pipeline management, and integrations with other critical tools. It simplifies the process of organizing contacts, prospecting, and nurturing relationships, making it ideal for sales, recruiting, fundraising, and partnership management.', NULL, '["Contact Management","Relationship Building","Sales Tools"]');
