INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9aa95c3a-a55f-45d7-a762-83c25afaf5b1', '2024-04-12T15:28:12.792Z', 'Freepik', 'https://www.freepik.com/ai/image-generator', 'images/freepik.webp', 'images/freepik-icon.webp', '{"what":"Freepik AI Image Generator is a tool that allows users to transform text into stunning images using artificial intelligence.","who":"This product is for individuals and businesses looking to easily create high-quality art, illustrations, and designs without the need for artistic skills.","best_features":["AI-powered image generation for quick and easy creation of visual content","Wide range of styles to choose from, including photo, digital art, painting, and 3D","User-friendly interface for seamless text-to-image transformation"],"pricing":[]}', 'The AI Image Generator by Freepik is a free tool that empowers users to turn their ideas into captivating art and designs. Users can create up to three AI-generated images per day, each with four different variations, by simply inputting text descriptions. The tool offers a wide range of styles and themes to inspire creativity and imagination.', NULL, '["AI Solutions","Image Generation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cf420823-bfa4-4ad9-be47-e13027a8a6c3', '2024-04-12T15:28:12.792Z', 'Freeplay', 'https://freeplay.ai/', 'images/freeplay.webp', 'images/freeplay-icon.webp', '{"what":"Freeplay is a tool designed to transform the way product teams build with Language Learning Models (LLMs), offering faster prototyping, confident testing, and optimized product features for customers.","who":"Freeplay is designed for product teams looking to experiment, test, monitor, and optimize AI features for customers, providing a comprehensive solution for managing the end-to-end LLM product development lifecycle.","best_features":["Ability to automate testing for prompts, chains, or chatbots using AI auto-evaluations for instant understanding.","Launch human labeling jobs to increase user trust and curate test cases from real results or custom uploads.","Observing all environments to provide insights for optimizing quality and cost, leveraging AI and human feedback for constant improvement."]}', 'Freeplay is a tool that empowers product teams to prototype like professionals, manage, version, and restore all prompts, track results, test different LLMs, automate testing, and launch human labeling jobs. It provides insights to optimize quality and cost, supports collaboration, and offers developer SDKs for Python, Node, and Java.', NULL, '["Productivity Tools","Developer Tools","AI Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('188f9876-b2d9-4f79-8546-b1d6f2c8c380', '2024-04-12T15:28:12.792Z', 'FreeTTS', 'https://freetts.com/', 'images/freetts.webp', 'images/freetts-icon.webp', '{"what":"FreeTTS is a text-to-speech software that allows users to convert written text into speech with customization options.","who":"This product is for content creators, podcasters, video creators, and anyone looking to add voiceover to their content.","best_features":["Supports multiple languages and accents","Offers a range of voices including male and female options","Customization options for adjusting speed and volume of speech"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"One-time","original_price":0,"discount":"N/A","features":["Text to speech in natural voices","Free for commercial use","2000 characters per week limit"]}]}', 'FreeTTS is a text-to-speech software that allows users to convert written text into speech with support for multiple languages and accents. Users can choose from a variety of voices, customize speed and volume, and use it for voiceovers in podcasts or videos. It is available as a web-based tool.', NULL, '["Text-to-Speech"]');
