INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e414ffa4-1f0b-4294-b7c7-5c1fa3bfee4f', '2024-04-12T15:28:12.798Z', 'Fusion Brain', 'https://editor.fusionbrain.ai/', 'images/fusion-brain.webp', 'images/fusion-brain-icon.webp', '{"what":"Fusion Brain is an AI-powered image editing application that emphasizes responsible usage and compliance with legal and ethical standards. It requires JavaScript to function and implements filters and moderation methods to prevent misuse.","who":"Fusion Brain is designed for individuals and businesses looking to create and edit images using AI technology in a responsible and ethical manner. Users must comply with the User Agreement and adhere to legal and ethical guidelines.","best_features":["AI-powered image editing capabilities","Responsible platform with filters and moderation methods","Emphasis on compliance with legal and ethical standards"]}', 'Fusion Brain is an application that allows users to create and edit images using artificial intelligence (AI) technology. It emphasizes responsible use and compliance with legal and ethical standards. The tool requires JavaScript to be enabled and implements filters and moderation methods to prevent illegal or unethical usage.', NULL, '["AI Tools","Art Generation","Software Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('991b9424-9fbb-4038-8166-df8530668514', '2024-04-12T15:28:12.798Z', 'FutureKid', 'https://futurekid.ai/', 'images/futurekid.webp', 'images/futurekid-icon.webp', '{"what":"FutureKid is an AI-powered tool that generates pictures of your future children using just one image of each parent. It provides realistic HD images that offer a sneak peek into your future family.","who":"FutureKid is for individuals or couples who are curious about what their future family might look like visually. It caters to those who want to explore and imagine their future child''s appearance without the need for actual children.","best_features":["AI technology that generates 12 high-quality pictures of predicted male and female children","Results delivered via email within one hour","High value on user privacy with data completely deleted from servers within 24 hours"],"pricing":[]}', 'FutureKid is an AI-powered tool that generates pictures of your future children using just one image of each parent. It provides realistic HD images that offer a sneak peek into your future family. Users upload a single photo from the mother and father, and the AI technology generates 12 high-quality pictures, with six for the predicted male child and six for the predicted female child. Results are delivered via email within one hour, and user data is deleted from servers within 24 hours to ensure privacy.', NULL, '["AI Tools","Parenting"]');
