INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('19712028-eaac-4d89-bcb1-adf2408340f9', '2024-04-12T15:28:12.816Z', 'Go Boldly', 'https://gobold.ly/', 'images/go-boldly.webp', 'images/go-boldly-icon.webp', '{"what":"Go Boldly is a mobile application designed to promote financial literacy through personalized, engaging, and informative learning experiences. It aims to equip users with the knowledge and resources to make informed financial choices and achieve long-term prosperity.","who":"This product is for individuals looking to improve their financial literacy, regardless of their background or current financial situation. It is suitable for beginners who want to learn about budgeting, investing, and other essential financial aspects.","best_features":["AI-powered learning that breaks down complex financial challenges into easy-to-understand modules.","Lessons taught by industry specialists in bite-sized, tailored videos for personalized learning.","Interactive quizzes to reinforce financial understanding and mastery at the user''s own pace."],"pricing":[]}', 'Go Boldly is a mobile app that aims to empower users with the knowledge and resources to make informed financial decisions and achieve long-term prosperity. It utilizes AI-powered learning to break down financial challenges into accessible modules, taught by industry specialists. Users can reinforce their knowledge through interactive quizzes and receive personalized guidance from experienced users. The app aims to instill savvy financial management habits as a natural part of one''s life.', NULL, '["Education","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f09395cb-008d-45b9-9147-792561a21920', '2024-04-12T15:28:12.817Z', 'GoalFocus', 'https://goalfocus.io/', 'images/goalfocus.webp', 'images/goalfocus-icon.webp', '{"what":"Goalfocus is a task management and to-do list app designed to help users prioritize daily tasks towards achieving their goals. It incorporates features like setting SMART goals, using the Eisenhower Matrix for task prioritization, and providing daily motivational quotes.","who":"This product is for individuals who want to improve their productivity, focus on goal achievement, and overcome procrastination. It is suitable for professionals, students, entrepreneurs, and anyone looking to organize their tasks effectively.","best_features":["Setting SMART goals and breaking them down into smaller tasks for better focus and progress.","Utilizing the Eisenhower Matrix for task prioritization based on importance and urgency.","Incorporating anti-procrastination measures like timeboxing to ensure timely task completion."]}', 'Goalfocus is a task management and to-do list app that helps users prioritize tasks based on their ultimate objectives. It encourages purposeful task execution through setting SMART goals, utilizing the Eisenhower Matrix, and providing daily motivational quotes. The app also includes anti-procrastination measures like timeboxing and offers weekly, monthly, and yearly review features for organized planning and tracking progress.', NULL, '["Task Management","Goal Achievement"]');
