INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ec7d9b72-45f2-45c5-a36a-973a9419aeaa', '2024-04-12T15:28:12.829Z', 'GPUrich', 'https://www.gpurich.ai/', 'images/gpurich.webp', 'images/gpurich-icon.webp', '{"what":"GPURich is an AI tool designed to simplify the process of finding and acquiring GPUs for users by scanning various GPU providers worldwide and notifying users when the desired GPU is found.","who":"GPURich is for individuals or businesses in need of GPUs for various purposes such as data analysis, AI development, gaming, and more.","best_features":["AI-powered GPU search and notification system","Customizable bidding system for users to set their desired price","Direct SSH access to the machine once a suitable GPU is found"],"pricing":[{"plan":"Starter","price":0,"billing_cycle":"Per GPU bid","original_price":0,"discount":"N/A","features":["Bid per GPU","Email notifications","Community support"]},{"plan":"Pro","price":250,"billing_cycle":"Monthly","original_price":250,"discount":"N/A","features":["Assistance in setting up the machine","Dedicated support from an engineer"]}]}', 'GPURich is an AI tool that simplifies the process of finding and acquiring GPUs for users. It scans various GPU providers worldwide and notifies users when the desired GPU is found. Users can create bids for GPUs by specifying the desired price and other parameters. Once a matching GPU is available, users receive email notifications and gain direct SSH access to the machine. GPURich offers different plans, including a Starter plan for bidding per GPU and a Pro plan with additional benefits.', NULL, '["GPU Acquisition","Cloud Computing","Bidding System"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cb385e7c-b403-42d3-8134-34eb525e19fb', '2024-04-12T15:28:12.829Z', 'GrabText', 'https://www.grabtext.ai/', 'images/grabtext.webp', 'images/grabtext-icon.webp', '{"what":"GrabText is an AI-powered tool that automates the conversion of text from images, documents, and graphics into editable and searchable formats. It features Online OCR functionality that recognizes handwritten, printed, and LaTeX text, offering a hands-off approach to transcription and a significant improvement to productivity.","who":"GrabText is designed for students, professionals, writers, developers, and anyone in need of efficient digitization, transcription, and searchability in their textual documents. It caters to a wide range of users with varying needs for text extraction and correction.","best_features":["Automated conversion of text from images, documents, and graphics into editable formats.","Automatic spelling and grammar corrections for greater text accuracy.","Versatile export options including TXT, DOC, and PDF files."]}', 'GrabText is an AI-powered tool that automates text extraction from images, documents, and graphics, offering Online OCR functionality for recognizing various types of text. It also features automatic spelling and grammar corrections, versatile export options, and ChatGPT technology for enhanced processing capabilities. The tool is adaptable for students, professionals, writers, and developers, providing a practical solution for digitization, transcription, and searchability in textual documents.', NULL, '["Text-to-Text","Transcription","OCR"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('dca4fa05-4afd-41e8-9416-03dddca305d2', '2024-04-12T15:28:12.829Z', 'GradesAI', 'https://gradesai.com/', 'images/gradesai.webp', 'images/gradesai-icon.webp', '{"what":"GradesAI is an educational AI tool designed to enhance study efficiency and improve student grades by creating personalized study plans, predictive exams, and various study tools.","who":"GradesAI is for students of all levels who want to boost their grades, improve their study habits, and have a more efficient and effective learning experience.","best_features":["Predictive Exam Generator™ for personalized practice tests","User-friendly dashboard for easy navigation and progress tracking","Flashcard generator and essay writing support for comprehensive study materials"],"pricing":[]}', 'GradesAI is an educational AI tool that offers personalized study plans, predictive exams, and various study tools to help students enhance their study efficiency and improve their grades. It predicts exam materials, generates custom practice tests, flashcards, and essays, and provides instant feedback on performance. With features like a user-friendly dashboard and a variety of study tools, GradesAI aims to make studying effortless and effective for students of all levels.', NULL, '["AI Tools","Study Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0653eb0f-8646-4fe7-9df3-34098b522364', '2024-04-12T15:28:12.830Z', 'GradeWrite', 'https://www.gradewrite.ai/', 'images/gradewrite.webp', 'images/gradewrite-icon.webp', '{"what":"GradeWrite is an AI tool designed to assist educators in grading assignments and essays efficiently. It offers bulk uploads, auto-checkers, personalized feedback, and custom rubrics.","who":"GradeWrite is tailored for educators who want to streamline their grading process, provide personalized feedback to students, and save time on grading assignments.","best_features":["Bulk uploads and 5000+ word limit for efficient grading","AI-enhanced, custom writing feedback for personalized suggestions","Auto-checkers for word count, AI detection, and rubric adherence for accuracy"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Bulk uploads and 5000+ word limit","AI-enhanced, custom writing feedback","Auto-checkers for word count, AI detection, and rubric adherence"]}]}', 'GradeWrite is an AI tool designed to streamline the grading process for educators. It offers features like bulk uploads, auto-checkers, personalized feedback, and custom rubrics to enhance the grading experience. With GradeWrite, educators can grade assignments efficiently, provide constructive feedback to students, and save time on grading tasks.', NULL, '["AI Tools","Productivity Tools","Writing Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('53c67937-4d21-4de9-96ff-88655f747d0f', '2024-04-12T15:28:12.830Z', 'GradienMood', 'https://gradienmood.posandu.me/', 'images/gradienmood.webp', 'images/gradienmood-icon.webp', '{"what":"GradienMood is an AI tool that generates calming purplish color gradients to enhance visual mood. It offers over 3600 global quotas for gradient mood generation. The tool features a linear gradient mode that can produce gradients from right to left, and allows for easy copy-pasting. The project is made by a young developer from Posandu and fueled by community support.","who":"GradienMood is for individuals or businesses looking to enhance visual mood through calming purplish color gradients. It is suitable for designers, artists, content creators, and anyone who wants to incorporate soothing color gradients into their projects.","best_features":["Generates over 3600 global quotas for gradient mood generation, providing a wide variety of options to choose from.","Linear gradient mode allows for easy customization of gradients from right to left, offering flexibility in design.","Community-supported project by a young developer from Posandu, adding a personal touch and fostering a sense of community."],"pricing":{"plan":"","price":0,"billing_cycle":"","original_price":0,"discount":"","features":[]}}', 'GradientMood is an AI tool that generates calming purplish color gradients to enhance visual mood. With over 3600 global quotas for gradient mood generation and a linear gradient mode for easy customization, this tool is perfect for designers, artists, and content creators looking to incorporate soothing color gradients into their projects. Developed by a young developer from Posandu and supported by the community, GradientMood offers a unique and personal touch to the world of color generation.', NULL, '["Art Generation","Design","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1ec34173-61f0-4bad-909b-ac342d098a25', '2024-04-12T15:28:12.830Z', 'Gradient.AI', 'https://gradient.ai/', 'images/gradient-ai.webp', 'images/gradient-ai-icon.webp', '{"what":"Gradient.AI is a powerful AI tool that enables users to fine-tune and generate completions on state-of-the-art open source models like Llama2 with just a single line of code. It allows for customization and deployment of private LLMs via web APIs, supporting multiple programming languages.","who":"Gradient.AI is designed for developers and businesses looking to leverage advanced AI capabilities for building and owning their AI tools and applications. It is suitable for those seeking to streamline model development and deployment processes.","best_features":["Fine-tuning of models for personalized use cases","Support for multiple programming languages like JavaScript, Python, and Java","Automated process and simplification of model development"],"pricing":[]}', 'Gradient.AI is a tool that provides everyday AI capabilities through simple web APIs, allowing users to customize and deploy private LLMs. It supports fine-tuning of models and offers state-of-the-art open-source models like Llama2. With features like multiple programming language support and automation of model development, Gradient empowers developers to build and own their AI tools.', NULL, '["Developer Tools","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0d6e3c17-3296-44e2-8080-1b59d6fbf2e6', '2024-04-12T15:28:12.830Z', 'Gradientj', 'https://gradientj.com/', 'images/gradientj.webp', 'images/gradientj-icon.webp', '{"what":"GradientJ is an AI tool designed to facilitate the building and management of NLP applications using Large Language Models (LLMs) like GPT-4. It aims to make NLP applications more accessible through the power of LLMs.","who":"GradientJ is ideal for developers, data scientists, and businesses looking to create and manage NLP applications efficiently. It is also suitable for teams working on AI projects that require complex orchestration and integration of multiple models.","best_features":["AI that learns with you: GradientJ provides an app-building canvas for quickly creating complex applications.","Build complex apps, fast: The tool takes care of orchestrating integrations, making it easy to build applications quickly.","Collaborate with your team: After deployment, GradientJ allows for team collaboration in managing and configuring applications across multiple customers."],"pricing":[]}', 'GradientJ is an AI tool that simplifies the development and management of NLP applications using Large Language Models like GPT-4. It enables users to test, deploy, and fine-tune their models, integrate proprietary data, and orchestrate complex applications with ease. The tool offers features such as prompt orchestration, live user feedback, and A/B testing to enhance model accuracy and efficiency.', NULL, '["NLP Applications","LLM Integration","Model Orchestration"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('54c70a81-63ad-497d-9507-589414a2779b', '2024-04-12T15:28:12.830Z', 'Grail Writer', 'https://www.grailwriter.com/', 'images/grail-writer.webp', 'images/grail-writer-icon.svg', '{"what":"Grail Writer is an artificial intelligence tool designed for generating SEO-optimized blog articles based on target keywords. It simplifies the content creation process with one-click operation.","who":"Grail Writer is for bloggers, content creators, marketers, and SEO specialists who need efficient and quality content generation for their websites.","best_features":["Automatically generates SEO-optimized blog articles based on target keywords.","User-friendly with one-click operation for easy content creation.","Powered by the OpenAI API for advanced content generation capabilities."],"pricing":[{"plan":"Open Beta","price":0,"billing_cycle":"Free","original_price":0,"discount":"100% off","features":["Unlimited usage during Open Beta","Requires user''s OpenAI API key","Users responsible for any API charges"]}]}', 'Grail Writer is an AI tool that generates SEO-optimized blog articles based on target keywords. It simplifies content creation with one-click operation and is powered by the OpenAI API. Users need their OpenAI API key to use the tool, and it is free to use during the Open Beta.', NULL, '["SEO","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d840ef67-2f9e-474e-ad94-da8065ca7532', '2024-04-12T15:28:12.831Z', 'Gramara', 'https://gramara.com/en', 'images/gramara.webp', 'images/gramara-icon', '{"what":"Gramara is an AI-powered grammar checker tool called ai-pow that helps improve grammar, diction, and writing style instantly. It supports multiple languages and can translate poor English into fluent, natural sentences.","who":"Gramara is for anyone looking to enhance their writing skills, improve grammar, diction, and writing style. It is suitable for students, professionals, writers, and anyone who wants to communicate effectively in English.","best_features":["Instant grammar correction and improvement","Support for multiple languages and translation capabilities","Highlighting of incorrect or awkward words for better writing fluency and clarity"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Instant grammar correction","Translation capabilities","Highlighting incorrect words"]}]}', 'Gramara is an AI tool that serves as a grammar checker called ai-pow, designed to enhance grammar, diction, and writing style instantly. It supports multiple languages, offers translation services, and helps improve writing fluency and clarity. Provided by Vectica System Ltd, Gramara is a free-to-use tool that aims to assist users in writing more effectively.', NULL, '["AI Tools","Language Support"]');
