INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cc9c01af-2fbb-4d91-b58a-28e6d68fc3c8', '2024-04-12T15:28:12.831Z', 'GrapeGPT', 'https://grapegpt.wine/', 'images/grapegpt.webp', 'images/grapegpt-icon.webp', '{"what":"GrapeGPT is an AI Wine Assistant tool that acts as a personal sommelier, providing wine recommendations and assistance with wine selection. It utilizes AI technology to understand user preferences and offer personalized wine choices.","who":"GrapeGPT is designed for wine enthusiasts, individuals looking for wine pairing recommendations, and anyone seeking assistance in selecting the perfect wine for their meals.","best_features":["AI Smart: GrapeGPT''s AI technology understands user taste preferences and suggests the best wine choices.","Chat Interactive: Users can engage in real-time conversations about their wine selections, enhancing their overall experience.","Testimonials: GrapeGPT includes testimonials from satisfied users, showcasing its effectiveness in revolutionizing the wine selection process."],"pricing":[]}', 'GrapeGPT AI Wine Assistant is a tool that acts as your personal sommelier, providing wine recommendations and assistance with wine selection. With GrapeGPT, users can take a picture of their wine list or engage in a conversation to receive guidance on the best wine pairing for their meal.', NULL, '["Wine","Recommendation","Chat"]');
