INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7ee0c1ee-96f5-44d4-a9eb-0eddea5b0ab5', '2024-04-12T15:28:12.859Z', 'HoneyDo', 'https://www.heyhoneydo.com/', 'images/honeydo.webp', 'images/honeydo-icon.webp', '{"what":"HoneyDo is a voice-powered grocery list application enhanced with artificial intelligence, designed to simplify the task of grocery shopping by transforming spoken words into an organized shopping list.","who":"HoneyDo is for individuals and families looking to streamline their grocery shopping experience, improve communication, and enhance organization when it comes to shopping for household essentials.","best_features":["Voice-powered grocery list creation for easy and hands-free list building.","Shared list creation and synchronization across multiple devices for seamless collaboration.","''Pic to Pick'' technology for snapping pictures of dishes and recipes to automatically add ingredients to the list."]}', 'HoneyDo is a voice-powered grocery list application enhanced with artificial intelligence. It simplifies grocery shopping by transforming spoken words into an organized shopping list. With features like shared list creation, ''Pic to Pick'' technology, and support for multiple languages, HoneyDo is designed to make grocery shopping easier and more efficient.', NULL, '["AI Assistance","Food & Drink"]');
