INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('da6b4f0b-cdb1-43cb-8fcf-a32cc1b2f9be', '2024-04-12T15:28:12.866Z', 'Hypemaan Songwriting', 'https://hypemaan.com', 'images/hypemaan-songwriting.webp', 'images/hypemaan-songwriting-icon.webp', '{"what":"Hypemaan Songwriting is an innovative app designed to elevate the lyric-crafting experience by providing smart suggestions, rhyme assistance, and seamless collaboration features for songwriters.","who":"This product is for passionate creators and songwriters looking to enhance their songwriting process with the help of AI suggestions and intuitive design.","best_features":["Intuitive Interface with AI Assistant: Seamlessly combines an intuitive user interface with cutting-edge AI suggestions.","On-the-Go Editing: Allows users to capture inspiration anywhere, anytime with quick jotting and editing features.","Export in Various Formats: Enables users to adapt to their preferred workflow by exporting lyrics in multiple formats."],"pricing":[]}', 'Hypemaan Songwriting is a powerful iOS app that revolutionizes the songwriting experience by providing smart suggestions, rhyme assistance, and seamless collaboration features. The app''s intuitive design and AI suggestions help users write quality lyrics with precision, while on-the-go editing and multiple format export features make it easy to capture inspiration and adapt to preferred workflows.', NULL, '["AI Assistance","Writing Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('66875324-2646-4433-b33f-42e637eb9d0c', '2024-04-12T15:28:12.866Z', 'Hyper', 'https://gethyper.ai/', 'images/hyper.webp', 'images/hyper-icon.webp', '{"what":"Hyper is an AI-powered tool designed to revolutionize data retrieval and interaction in a business setting by seamlessly connecting with various applications to provide actionable insights.","who":"Hyper is ideal for businesses looking to streamline their data retrieval processes, enhance decision-making, and improve overall productivity through AI-powered technology.","best_features":["Seamless integration with a wide array of platforms and applications such as Google Drive, Slack, Salesforce, and more.","Live data support for real-time relevance and accuracy in decision-making processes.","API-driven search functionality for query-driven interaction with apps like Gmail and Salesforce."],"pricing":[]}', 'Hyper is an AI-powered tool engineered to seamlessly connect with other applications to form a comprehensive and dynamic communication interface. It reinvents how data retrieval and interaction occur in a business setting by turning dynamic data into actionable insight. Hyper allows users to conduct chat and searches across integrated platforms, accommodating live data for decision-making processes and contextualizing relevant information.', NULL, '["Data Analysis","Business Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('475bc94f-c587-4872-be0a-c9d7606838c8', '2024-04-12T15:28:12.866Z', 'Hypera', 'https://www.hypera.app/', 'images/hypera.webp', 'images/hypera-icon.webp', '{"what":"Hypera is an AI-powered tool designed to assist with email marketing by generating hyperpersonalized email introductions. The primary purpose of this tool is to enhance the personalization of cold emails, thereby aiming to improve click-through rates (CTR).","who":"Hypera is for businesses looking to scale their influencer outreach strategies or enhance the effectiveness of cold email marketing.","best_features":["AI-powered technology for hyperpersonalized email introductions","Tailored and engagement-boosting email intros for YouTube influencer outreach","Three pricing tiers to cater to different business needs"]}', 'Hypera is an AI-powered tool that generates hyperpersonalized email introductions for YouTube influencer outreach and cold email marketing. It uses AI to analyze YouTube channel URLs and create tailored email intros to boost engagement. With three pricing tiers, Hypera caters to small, medium, and large businesses looking to enhance their influencer outreach strategies.', NULL, '["AI Tools","Marketing"]');
