INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7912891d-63f8-4636-b4ed-d5774bad0545', '2024-04-12T15:28:12.872Z', 'Ideapad', 'https://ideapad.live/', 'images/ideapad.webp', 'images/ideapad-icon.webp', '{"what":"Ideapad is a platform designed to help creators, beginners, and side-hustlers validate their ideas before investing in full development, by offering tools for idea validation, pre-orders, and early access deals.","who":"Ideapad is for creators, beginners, and side-hustlers who want to validate their ideas efficiently and cost-effectively, with a strong emphasis on user-friendliness and AI integration.","best_features":["Streamlined dashboard for clear insights into user engagement and revenue generation.","Integration with payment platforms like Stripe, Gumroad, and Lemon Squeezy for pre-orders and early access deals.","AI integration for quicker insights, summarizing user research calls, and efficient understanding of customers."]}', 'Ideapad is a platform that helps creators validate their ideas before reaching the Minimal Viable Product (MVP) stage, by offering tools for idea validation, pre-orders, and early access deals. It simplifies the process with a streamlined dashboard, integrates with payment platforms for pre-orders, and leverages AI for quicker insights and user research summarization. Ideapad is designed for creators, beginners, and side-hustlers, aiming to cut down on validation time and ensure efficient pursuit of viable projects.', NULL, '["Startup Resources","AI Tools","Productivity Tools"]');
