INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c3662548-4f93-4d62-b6a6-67ead2a0c7bb', '2024-04-12T15:28:12.885Z', 'Indise', 'https://indise.io/', 'images/indise.webp', 'images/indise-icon.webp', '{"what":"Indise is an AI-powered interior design tool that allows users to quickly and effortlessly generate customized interior design concepts in under 90 seconds.","who":"This product is for individuals looking to create their dream interior designs efficiently and visually appealingly.","best_features":["AI-powered tool for quick and effortless interior design concept generation","User-friendly interface for easy customization and editing of designs","Ability to deliver final interior design results in less than 90 seconds"],"pricing":[]}', 'Indise is an AI-powered interior design tool that enables users to generate customized interior design concepts quickly and effortlessly. Users can create their dream interior designs in under 90 seconds by providing a detailed description, selecting square footage, choosing a design style, and uploading reference images. The tool generates four pre-designed results in just 60 seconds, which users can further edit or upscale for higher resolution. The final design is delivered in less than 90 seconds, making it a convenient solution for efficient and visually appealing interior design options.', NULL, '["AI Agents","Design","Art Generation"]');
