INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3fdf9884-4336-40f9-93da-21b13d63b4b5', '2024-04-12T15:28:12.928Z', 'Kaba', 'https://kaba.ai/', 'images/kaba.webp', 'images/kaba-icon.webp', '{"what":"Kaba is an AI-native operating system designed to enhance the intelligence and creativity of humanity''s digital and physical worlds.","who":"Kaba is for individuals and enterprises looking for a secure, private, and AI-driven operating system that prioritizes user learning and creativity.","best_features":["AI-first architecture for personalized data presentation tailored to user''s style","Advanced policy controls for privacy and security with data partitioning options","Polymorphic operating system for immersive computing environment and creativity enhancement"],"pricing":[]}', 'Kaba is an AI-native operating system that embeds AI at its core to revolutionize the future of computing. It offers a private, secure, and personalized experience for users, focusing on privacy, security, user learning, and creativity.', NULL, '["AI","Privacy & Security","Creativity Enhancement"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b1fe8de1-108d-4bb2-a020-78abf41aa695', '2024-04-12T15:28:12.929Z', 'Kadoa', 'https://www.kadoa.com/', 'images/kadoa.webp', 'images/kadoa-icon.svg', '{"what":"Kadoa is an AI-powered web scraper that automates data extraction workflows from various sources, saving time and effort for users. It offers smart navigation, RPA, and autonomous agents to locate desired information on websites, along with self-healing maintenance capabilities.","who":"Kadoa is designed for organizations and individuals who need to extract data from websites for various purposes such as e-commerce, finance, and job boards. It is suitable for those looking to automate their data extraction processes and ensure compliance with industry regulations.","best_features":["No Coding Required: Build data extraction workflows 10X faster without any coding or browser extensions.","Smart Navigation and RPA: Autonomous agents locate desired information on websites and handle clicking, scrolling, and maintenance workflows.","Enterprise Scalability: Process millions of data records daily without compromising quality or speed."],"pricing":[]}', 'Kadoa is an AI-powered web scraper that enables users to extract data from various sources easily. It offers smart navigation, RPA, and autonomous agents to automate data extraction workflows for use cases such as e-commerce, finance, and job boards. The tool is scalable, reliable, and offers customizable transformation rules, along with a user-friendly API for easy integration into projects and tools.', NULL, '["Automation","E-commerce","Finance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e5eebba5-8007-4c14-9aa5-9a1ed8277d62', '2024-04-12T15:28:12.929Z', 'Kahma', 'https://kahma.io/', 'images/kahma.webp', 'images/kahma-icon.webp', '{"what":"Kahma.io is an AI-powered tool that generates digital portraits and avatars from source images, such as real-life photos. Users can create personalized, lifelike digital twins of themselves, their pets, or loved ones, even ones who have passed away.","who":"This product is for individuals looking to create unique and high-quality digital portraits and avatars of themselves, their loved ones, or deceased relatives.","best_features":["AI technology that generates unique and lifelike portraits and avatars","8K quality resolution for high-definition images","Ability to create portraits and avatars from source images of real-life photos"]}', 'Kahma.io is an AI-powered tool that allows users to create personalized digital portraits and avatars from source images. The generated images are of high quality, available in 8K resolution, and can be used for personal keepsakes, social media profiles, or professional projects. Users can easily download the images once created, and there are no limitations on their use. The service is available through a membership subscription with various pricing plans and options.', NULL, '["AI Tools","Image Editing","Avatar"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d14c5309-1e1e-4b45-ba40-9fdecdd804ab', '2024-04-12T15:28:12.929Z', 'Kahuna', 'https://joinkahuna.com', 'images/kahuna.webp', 'images/kahuna-icon.webp', '{"what":"Kahuna is an AI tool designed to help experts in various fields sell monthly subscriptions to their knowledge and expertise. It is a platform that allows experts to provide personalized advice based on their unique knowledge base, manage subscriptions and questions through an online dashboard, and scale up their advice-giving abilities.","who":"Kahuna is for experts in different industries who want to monetize their knowledge and expertise by offering personalized advice to subscribers. It is also suitable for individuals looking to access expert advice on a monthly subscription basis.","best_features":["Personalized advice based on expert''s unique knowledge base","Subscription tiers based on the number of monthly questions to be answered","Simple online dashboard for managing subscriptions and questions"],"pricing":[]}', 'Kahuna is an AI tool that empowers experts to monetize their knowledge and expertise by offering personalized advice through monthly subscriptions. It allows experts to scale up their advice-giving abilities, create a shareable profile, and manage subscriptions and questions efficiently through an online dashboard.', NULL, '["Expert Advice","Subscription Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b2b805a4-22be-48c4-bff9-4862eb51ba24', '2024-04-12T15:28:12.929Z', 'Kai App', 'https://trykai.app/', 'images/kai-app.webp', 'images/kai-app-icon.webp', '{"what":"KAI | KeyboardAI is an AI tool designed to help iPhone users write more efficiently and creatively through their keyboards.","who":"This product is for writers, bloggers, students, people with disabilities who find typing challenging, and anyone looking to enhance their writing skills and efficiency.","best_features":["AI-powered auto-correction, word prediction, and auto-completion for improved writing efficiency","Integration with CHATGPT for engaging in natural language conversations","Time-saving and quality content creation with minimal effort"],"pricing":[]}', 'KAI | KeyboardAI is an AI tool that enhances writing skills and efficiency for iPhone users. It offers features like auto-correction, word prediction, and integration with CHATGPT for natural language conversations.', NULL, '["Writing Assistance","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d5789098-57d9-4047-8475-72c89ea8d005', '2024-04-12T15:28:12.930Z', 'kAI KGApps', 'https://kai.kgappss.com/', 'images/kai-kgapps.webp', 'images/kai-kgapps-icon.webp', '{"what":"kAI KGApps is an AI-powered task organizer designed to streamline daily routines by automating task organization and providing productivity tips.","who":"This product is for individuals looking to enhance their organization skills, improve productivity, and efficiently manage their daily tasks.","best_features":["Automated task organization to eliminate manual planning and save time.","Customized notifications for task reminders to keep users on track.","Regularly updated blog content with productive tips and guides for users."],"pricing":[]}', 'kAI KGApps is an AI-powered task organizer that transforms daily routines by automating task organization and providing productive tips through its blog content. It encourages ''block planning'' to improve focus and seamlessly integrates into existing workflows. With features like customized notifications and task reminders, kAI aims to enhance users'' organization and productivity levels.', NULL, '["AI Assistance","Task Management","Productivity Tips"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2a3f37e5-479e-424f-b4da-6960f70dd2c1', '2024-04-12T15:28:12.930Z', 'Kai', 'https://www.kaiconversations.ai/', 'images/kai.webp', 'images/kai-icon.webp', '{"what":"KAI is a conversational analysis tool that improves health outcomes by providing hidden human insights from remote conversations. It helps pharmaceutical organizations, healthcare professionals, and businesses gain valuable insights to enhance patient care and market access.","who":"KAI is designed for pharmaceutical organizations, healthcare professionals, sales teams, and businesses looking to improve customer insights, salesforce effectiveness, and coaching services.","best_features":["Provides insights to better understand the needs of healthcare professionals and increase market access","Improves patient outcomes and emotional intelligence of healthcare professionals","Monitors sales team performance in real-time to optimize conversational strategies"],"pricing":[]}', 'KAI is a revolutionary tool that leverages AI to analyze conversations and provide valuable insights for improving health outcomes. It is used by pharmaceutical organizations, healthcare professionals, and businesses to enhance customer insights, salesforce effectiveness, and coaching services.', NULL, '["Pharmaceuticals","Healthcare Services","Coaching Services"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8ab9178f-3883-4cb8-9b51-796804fd3909', '2024-04-12T15:28:12.930Z', 'KAIF Platform', 'https://kaif-platform.com/', 'images/kaif-platform.webp', 'images/kaif-platform-icon.webp', '{"what":"KAIF Platform is an AI-enabled decentralized society (DeSoc) application that provides users with a personalized AI assistant called Digital Twin to promote personal growth and enhance skills.","who":"This product is designed for individuals looking to improve their skills, education, health, and career, as well as businesses seeking to boost efficiency and access professional knowledge.","best_features":["Personalized AI assistant (Digital Twin) for personal and business use","Opportunity to earn rewards by completing missions from major companies","Secure digital identity (Digital Passport) based on non-transferable Soulbound Tokens (SBT)"]}', 'KAIF Platform is an AI-powered application that offers users a Digital Twin AI assistant to help with personal growth, skill enhancement, and completing missions from major brands. The platform also provides a secure digital identity through a Digital Passport based on non-transferable Soulbound Tokens (SBT). Users can store privacy data, skills, experiences, medical records, and social profiles, influencing brand evolution through surveys and tasks.', NULL, '["Personal Growth","Skill Enhancement","Digital Identity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a2b5de5f-0356-4ad4-a289-d32f3bf307f4', '2024-04-12T15:28:12.930Z', 'kajiwoto.ai', 'https://kajiwoto.ai/', 'images/kajiwoto-ai.webp', 'images/kajiwoto-ai-icon.webp', '{"what":"kajiwoto.ai is an AI tool focused on creating AI chat characters with advanced AI models, ChatGPT integration, AI voice feature, automatic sleep patterns, mood support, image album creation, chat rooms, private messaging, Discord bot integration, and developer support.","who":"This product is for individuals and businesses looking to create AI chatbots for companionship, gaming, roleplay, knowledge sharing, and various other purposes. It is also suitable for developers seeking AI chatbot integration solutions.","best_features":["Advanced AI models and ChatGPT integration for creating realistic AI chat characters.","AI voice feature for installing voices in chatbots, adding a new dimension to interactions.","Private messaging and chat rooms for confidential conversations and group interactions."],"pricing":[]}', 'kajiwoto.ai is an AI tool focused on creating AI chat characters with advanced features such as AI voice, mood support, image album creation, private messaging, and Discord bot integration. Users can interact with their chatbots in various settings and customize their characters for different purposes. The tool also offers developer support and continuous updates to enhance the chatbot experience.', NULL, '["Chat Solutions","AI Agents","Developer Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9869b581-0d8f-46de-979c-804ae9564d66', '2024-04-12T15:28:12.930Z', 'Kalami', 'https://kalami.ai/', 'images/kalami.webp', 'images/kalami-icon.webp', '{"what":"Kalami is an intuitive application that allows users to interact with OpenAI''s GPT-4 in a personalized way by uploading their own data to enhance contextual understanding and generate relevant responses.","who":"Kalami is designed for individuals and businesses looking to improve AI conversations, boost data analysis, enhance content creation, and provide personalized customer service without the need for technical knowledge.","best_features":["Supports upload of text files and CSV files for enhanced contextual understanding","Seamlessly trains and personalizes chats without APIs or third-party services","Prioritizes data security and privacy with stringent protocols"]}', 'Kalami is an intuitive application that enables users to interact with OpenAI''s GPT-4 in a uniquely personalized way. By uploading their own data, users can enhance the AI''s contextual understanding and generate more relevant and context-aware responses. The tool supports various file types, including PDF files, and allows users to share URLs of data sources for contextual references. Kalami is user-friendly, requires no technical knowledge, and can be used in various applications such as research, content creation, education, and customer service.', NULL, '["Data Analysis","Content Creation","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('89355781-bdc7-43a0-8a9e-3e4f07ab4a51', '2024-04-12T15:28:12.931Z', 'Kamara', 'https://kamaraapp.com/', 'images/kamara.webp', 'images/kamara-icon.svg', '{"what":"Kamara is an AI-powered coding tool that helps developers write and edit code more efficiently by translating instructions into code snippets using GPT-4 technology.","who":"Kamara is designed for developers looking to streamline their coding process and increase productivity by automating parts of the coding process.","best_features":["Efficient code writing and editing with AI technology","Ability to split long files into shorter ones for better results","Credit-based pricing system for flexible payment options"],"pricing":[{"plan":"Credit-based","price":10,"billing_cycle":"for every 200k credits","original_price":20,"discount":"","features":["Pay for how much code is read and written by Kamara","Flexible payment options","400,000 credits for $20"]}]}', 'Kamara is an AI-powered coding tool that leverages GPT-4 technology to help developers write and edit code more efficiently. It is a VS Code extension that translates instructions into code snippets, works best with TypeScript, and can split long files into shorter ones. Kamara uses a credit-based pricing system where developers pay for how much code is read and written. The tool aims to save time for developers by automating parts of the coding process.', NULL, '["AI Tools","Coding Assistance","Software Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0b1993fb-ee89-412d-bc72-9d350d0ed837', '2024-04-12T15:28:12.931Z', 'Kami', 'https://kamihome.com', 'images/kami.webp', 'images/kami-icon.webp', '{"what":"Kami Support Login & Download is an AI camera system designed for 24/7 fall detection assistance, providing real-time alerts for caregivers to respond quickly to falls and ensure the safety of their loved ones.","who":"This product is ideal for individuals who have elderly family members or loved ones who may be at risk of falling and require constant monitoring and assistance.","best_features":["Automatic fall detection without the need for additional sensors or panels","Real-time alerts for caregivers to respond promptly to falls","Affordable smart cameras for home security and peace of mind"]}', 'Kami Support Login & Download is an AI camera system that offers 24/7 fall detection assistance through automatic alerts for caregivers. It ensures the safety of loved ones by detecting falls without the need for extra sensors. Additionally, Kami provides a range of smart cameras for home security and a smart dash camera for on-the-road monitoring, making it easier to enhance safety and security.', NULL, '["AI Cameras","Fall Detection","Security Cameras"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('64206273-5042-4b8e-8f99-e9b2ea7f28a2', '2024-04-12T15:28:12.931Z', 'Kamoto', 'https://www.kamoto.ai/', 'images/kamoto.webp', 'images/kamoto-icon.webp', '{"what":"Kamoto.AI is a platform that allows users to create, train, and monetize AI characters and personalities, with a focus on offering licensed celebrities the opportunity to profit from their AI personas. The tool also provides an AI Characters and Personalities Marketplace for users to rent out their creations and generate passive income.","who":"Kamoto.AI is designed for individuals, celebrities, influencers, businesses, game developers, educational institutions, healthcare providers, legal firms, app developers, and hobbyists interested in creating and integrating AI characters and personalities into various industries and use cases.","best_features":["Create and customize unique virtual AI Characters or Personalities without technical expertise","Monetize AI creations through the AI Characters and Personalities Marketplace","Offer licensed celebrities the opportunity to profit from their AI personas"]}', 'Kamoto.AI is an AI tool that enables users to create, train, and monetize AI characters and personalities, with a focus on offering licensed celebrities the chance to profit from their AI personas. The platform includes an AI Characters and Personalities Marketplace for users to rent out their creations and generate passive income. Kamoto.AI caters to a diverse range of industries and use cases, providing a platform for individuals, businesses, game developers, educational institutions, healthcare providers, legal firms, and app developers to integrate AI characters and personalities seamlessly.', NULL, '["AI Characters","AI Marketplace","Celebrity Licensing"]');
