INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('efb7deb1-2552-476b-987f-84e04bf71009', '2024-04-12T15:29:21.329Z', 'Kansei', 'https://kansei.app/', 'images/kansei.webp', 'images/kansei-icon.webp', '{"what":"Kansei is an AI-powered language learning tool that offers immersive conversations with lifelike personas in multiple languages.","who":"Kansei is designed for language learners who want to practice and improve their skills through realistic interactions and personalized feedback.","best_features":["Adaptive Interaction: Kansei adapts to your learning style, offering natural conversations tailored to your preferences.","Instant Understanding: Overcome language barriers with on-the-spot translations to expand your vocabulary effortlessly.","Tailored Progress: Receive real-time feedback on grammar and spelling to enhance your language mastery."],"pricing":[]}', 'Kansei is a language learning platform that allows users to engage in real conversations with AI chat companions in Spanish, English, Italian, French, German, and Japanese. Through roleplaying and personalized practice, users can enhance their language skills in a fun and interactive way. Kansei offers adaptive interactions, instant translations, tailored progress tracking, and real-world immersion to create a unique language learning experience.', NULL, '["Language Learning","AI Chat Tool","Personalized Learning"]');
