INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('055223c7-fb72-4fcc-9035-ec0900579e88', '2024-04-12T15:29:21.331Z', 'karaok-AI', 'https://karaok-ai.neurospell.com/', 'images/karaok-ai.webp', 'images/karaok-ai-icon.webp', '{"what":"karaok-AI is an open-source karaoke player/editor that automatically creates clips from any song file by extracting vocals and lyrics using speech-to-text technology. It is a tool designed for music enthusiasts, karaoke lovers, DJs, and anyone interested in creating and editing karaoke tracks.","who":"This product is for music enthusiasts, karaoke lovers, DJs, and individuals who enjoy creating and editing karaoke tracks. It caters to both casual users looking for a fun karaoke experience and professionals in the music industry seeking a versatile tool for remixing and editing songs.","best_features":["Automatic clip creation from any song file by extracting vocals and lyrics using speech-to-text technology.","Includes a lyrics and timestamps editor, as well as a screener with synchronized lyrics for easy editing and playback.","Features kaiDJ, a minimalist and versatile DJ party player with multi-sound card support, song indexing, and playlist management capabilities."],"pricing":[]}', 'karaok-AI is an open-source karaoke player/editor that automatically creates clips from any song file by extracting vocals and lyrics using speech-to-text technology. It includes features like a lyrics and timestamps editor, a screener with synchronized lyrics, and kaiDJ, a versatile DJ party player. The tool is available for download on Windows, Mac, and Linux, and can also be accessed from Github.', NULL, '["Audio Editing","AI Tools"]');
