INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d418de94-c2dc-4e63-889b-ea5057ea8f13', '2024-04-12T15:29:21.341Z', 'Kiri.art', 'https://kiri.art/', 'images/kiri-art.webp', 'images/kiri-art-icon.webp', '{"what":"Kiri.art is an AI tool designed for users to easily generate images using default settings or credits. It also provides a bug reporting and feature request forum for users to address any issues or suggest enhancements. Additionally, users can create and share their own community star image history.","who":"Kiri.art is ideal for artists, designers, content creators, and anyone looking to quickly generate images using AI technology. It caters to individuals who want to streamline their image creation process and collaborate with others in the community.","best_features":["Easy image generation with default settings or credits","Bug reporting and feature request forum for user feedback","Community star image history creation and sharing"],"pricing":[]}', 'Kiri.art is an AI tool that simplifies the image generation process for users by offering default settings or credit-based options. It also includes a platform for users to report bugs, request new features, and create and share their own community star image history.', NULL, '["Art Generation","Community Collaboration","Image Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6781da19-1264-4a0c-bf2a-9c1187b1b777', '2024-04-12T15:29:21.341Z', 'Kirin', 'https://www.kirin3d.com/', 'images/kirin.webp', 'images/kirin-icon.webp', '{"what":"Kirin3d is an AI system designed to transform text prompts into tangible 3D printed objects.","who":"Kirin3d is for artists, designers, engineers, and individuals looking to bring their textual ideas to life through 3D printing.","best_features":["Seamlessly converts textual ideas into physical 3D models","Prioritizes accessibility and user-friendliness for all users","Efficiently maps text-based inputs into high-quality 3D printable models"],"pricing":[]}', 'Kirin3d is an AI system that bridges the gap between imagination and reality by allowing users to convert text prompts into tangible 3D printed objects. It leverages artificial intelligence to interpret and analyze text descriptions, enabling users to visualize and materialize their concepts through 3D printing. Kirin3d streamlines the design process, saves time and effort, and ensures high-quality output for a wide range of users, from artists to engineers.', NULL, '["AI Agents","Art Generation","Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2c46f013-c235-4f6d-b7b3-28f86b9c92e0', '2024-04-12T15:29:21.341Z', 'Kirsch', 'https://www.kirsch.ai/', 'images/kirsch.webp', 'images/kirsch-icon.webp', '{"what":"The Sign In tool by Kirsch is a user authentication system that allows users to securely access their accounts by entering their email address and password. It also offers the option to sign in using Facebook or Google credentials, as well as features for password reset and account registration.","who":"This product is designed for websites or applications that require user authentication, such as e-commerce sites, social media platforms, online banking services, and any other platform that needs secure access for its users.","best_features":["User-friendly interface with clear instructions for easy sign-in, registration, and password reset.","Integration with Facebook and Google authentication for added convenience.","Efficient and straightforward authentication system for secure account access."]}', 'The Sign In tool by Kirsch is a user authentication system that enables users to access their accounts securely. It provides options for signing in with email and password, Facebook, or Google credentials, as well as features for password reset and account registration. The tool offers a user-friendly interface with clear instructions, making it easy for users to navigate the authentication process.', NULL, '["Authentication","User Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3831bd40-1bac-4bc2-a331-c9945da49c4d', '2024-04-12T15:29:21.341Z', 'Kiru AI', 'https://kiru.ai/', 'images/kiru-ai.webp', 'images/kiru-ai-icon.webp', '{"what":"Kiru AI is an AI-powered recruitment tool designed to streamline the hiring process by automating candidate search, resume ranking, and workflow customization.","who":"Kiru AI is for HR professionals, recruiters, and hiring managers looking to enhance their recruitment efficiency and accuracy.","best_features":["AI Workflows for fast and accurate candidate search","Intelligent Candidate Matching for precise resume screening","Custom Workflows and smart triggers for end-to-end recruitment efficiency"],"pricing":[]}', 'Kiru AI is an AI-driven recruitment solution that integrates with job boards, automates workflows, and ranks resumes with precision. It offers advanced features like AI Workflows, Intelligent Candidate Matching, and custom workflows to enhance recruitment efficiency. The tool also includes resume parsing, automation scoring, and comprehensive candidate insights for a hassle-free hiring experience.', NULL, '["Recruitment","HR Tech"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('095b2ecb-a81c-47c5-afa3-ae33a71ce892', '2024-04-12T15:29:21.341Z', 'Kissinvestments', 'https://kissinvestments.com/youtube-clickbait-titles-generator/', 'images/kissinvestments.webp', 'images/kissinvestments-icon.webp', '{"what":"The YouTube Clickbait Titles Generator is a tool designed to help YouTubers create engaging and clickable titles for their videos. It assists in striking a balance between clickability and findability, crucial for video success on YouTube.","who":"This product is for YouTubers looking to increase their video views and attract more clicks by optimizing their video titles. It is also beneficial for content creators who struggle with coming up with catchy and compelling titles for their videos.","best_features":["Generates contextual title suggestions based on content topic and place or person","Backed by extensive research into trending themes, channels, and videos","Provides tips for maximizing clickability and findability"],"pricing":[]}', 'The YouTube Clickbait Titles Generator is a free tool that assists YouTubers in creating catchy titles for their videos to attract more clicks and views. It helps users strike a balance between clickability and findability, which are crucial for video success on YouTube. The tool provides tips such as utilizing listicles, emotional words, proper capitalization, numbers, and curiosity to craft engaging titles.', NULL, '["Video Editing","SEO","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7ac4f46e-926a-49f8-bbce-b75509edb47b', '2024-04-12T15:29:21.342Z', 'KitchenGPT', 'https://kitchengpt.io/', 'images/kitchengpt.webp', 'images/kitchengpt-icon.webp', '{"what":"KitchenGPT.io is an AI-based tool designed to facilitate a unique and brisk way of visualizing kitchen designs. It integrates advanced artificial intelligence technology to let you dynamically generate and envisage your ideal kitchen.","who":"This product is for individuals planning to remodel their kitchen but unable to decide or envision how certain themes will manifest in their own space. It is also suitable for those interested in exploring different kitchen design options effortlessly.","best_features":["Ability to manipulate the visual aspect of user-submitted kitchen photos into various themes","No user cost associated with utilizing the tool","Helps in illustrating potential changes or designs for one''s kitchen"],"pricing":[]}', 'KitchenGPT.io is an AI-based tool that allows users to submit an image of their existing kitchen and visualize how it could potentially appear under different themes. It is particularly helpful for individuals planning to remodel their kitchen but struggling to decide on a design. The tool is free to use and provides users with the ability to experiment with diverse kitchen setups in line with their preferred aesthetic. While the tool offers advanced AI-driven capabilities, the level of detail accuracy in the generated designs may vary.', NULL, '["AI Tools","Artificial Intelligence","Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('891e1d0c-8c1d-4a6c-8f62-853e83444ef1', '2024-04-12T15:29:21.342Z', 'KITI AI', 'https://www.kiti.ai', 'images/kiti-ai.webp', 'images/kiti-ai-icon.webp', '{"what":"Kiti AI is an AI-powered tool for creating and ordering recipe food kits. Users can build food kits from trending recipes or their own creations and easily access them through Hello Kiti login. The platform offers over 1,000 food kits ranging from easy recipes to dessert delights, curated by top creators in the community.","who":"Kiti AI is designed for food enthusiasts who enjoy exploring new recipes, trying out trending dishes, and creating their own food kits with ease. It caters to individuals looking for convenient ways to order and enjoy a variety of recipe kits.","best_features":["AI-powered tool for creating and ordering recipe food kits","Access to over 1,000 food kits curated by top creators","Simple feed us link for building food kits from trending recipes or personal creations"],"pricing":[]}', 'Kiti AI is an innovative platform that leverages AI technology to simplify the process of creating and ordering recipe food kits. Users can explore trending recipes, access over 1,000 curated food kits, and easily build their own kits with just a feed us link. With a focus on convenience and variety, Kiti AI is a go-to platform for food enthusiasts seeking new culinary experiences.', NULL, '["Recipe Kits","AI-powered Tool","Food Delivery"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5f067941-aaed-4a31-a563-affb559fc98e', '2024-04-12T15:29:21.342Z', 'Kits AI', 'https://www.kits.ai/', 'images/kits-ai.webp', 'images/kits-ai-icon.svg', '{"what":"Kits AI is an AI voice platform designed specifically for musicians, offering a variety of AI voices and the ability to create custom voice models.","who":"Kits AI is for musicians who want to enhance their creative output by transforming their voice using AI voices, creating custom voice models, and collaborating with other artists.","best_features":["AI Voice Cloning: Spend less time in the studio and more time creating using a custom digital clone of any voice.","AI Singing Generators: Select from a growing library of 35+ artists to put a new voice on your next track.","Vocal Remover: Isolate clean, production-ready vocals from any audio source, remove vocals, de-echo, de-reverb, and more."]}', 'Kits AI is an AI voice platform for musicians, offering a diverse array of AI voice options, the ability to create custom voice models, and access to officially licensed artist voices. It simplifies music workflows, allowing users to clone voices, sing like anyone, play any instrument, and more. Kits AI empowers musicians to streamline their vocal and audio workflow with studio-quality AI audio tools.', NULL, '["AI Tools","Artificial Intelligence","Entertainment"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1ac5bd47-302c-46c1-9e55-2fae1c9527bd', '2024-04-12T15:29:21.342Z', 'Kiwi Video', 'https://kiwi.video/', 'images/kiwi-video.webp', 'images/kiwi-video-icon.webp', '{"what":"Kiwi Video is an AI tool designed to enhance the learning experience of users by allowing them to watch videos, ask questions, receive summaries, and quiz themselves on YouTube content. It focuses on topics like motivation, focus, and perseverance to help users develop grit and improve their learning abilities.","who":"This product is ideal for students, professionals, and anyone looking to optimize their learning process by extracting more value from YouTube videos. It caters to individuals seeking to enhance their knowledge and skills in a convenient and efficient manner.","best_features":["Interactive learning experience through video watching and quizzes","Focused content on motivation, focus, and perseverance","Time-saving features for extracting key information from videos"],"pricing":[]}', 'Kiwi Video is an AI tool that revolutionizes the way users engage with YouTube videos. By offering features such as question-answering, summarization, and quizzing capabilities, it enables individuals to deepen their understanding and retention of content. With a specific emphasis on motivation, focus, and perseverance, Kiwi Video aims to empower users to develop grit and enhance their learning abilities. By maximizing the value extracted from YouTube videos, this tool is a valuable asset for those looking to optimize their learning journey.', NULL, '["AI Tools","Video Editing","Self-improvement"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bdb0b50a-445a-4818-98c6-cd0351ced7f4', '2024-04-12T15:29:21.343Z', 'Klangio', 'https://klang.io/', 'images/klangio.webp', 'images/klangio-icon.webp', '{"what":"Klangio is an AI-based software tool designed to automatically transcribe music from audio to sheet music, aiding in the notations of improvisations or compositions.","who":"Klangio is for musicians, composers, music teachers, artists, and bands who want to easily transcribe their music from audio to sheet music for learning, recording, or sharing purposes.","best_features":["AI-powered transcriptions with high accuracy","Versatile apps for piano, guitar, vocals, and pop songs","Export options in various formats like PDF, MIDI, and Guitar Pro"]}', 'Klangio is an innovative AI-based software tool that automatically transcribes music from audio to sheet music. It offers specialized apps like Piano2Notes, Guitar2Tabs, Sing2Notes, and Melody Scanner for different musical instruments and genres. Users can record or upload their performances, and the AI transcribes the music accurately. The transcribed music can be easily accessed, edited, and exported in various formats. Klangio caters to musicians, composers, teachers, and artists, providing a valuable tool for a range of musical applications.', NULL, '["AI Tools","Software","Artificial Intelligence"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('450cc3e6-809f-48fa-af30-99fc4611c604', '2024-04-12T15:29:21.343Z', 'Klap.app', 'https://klap.app/', 'images/klap-app.webp', 'images/klap-app-icon.webp', '{"what":"Klap App is an AI-powered tool that streamlines the process of creating short videos for platforms like TikTok, Reels, and Shorts. It allows users to transform YouTube videos into ready-to-publish TikToks with just a single click, leveraging AI technology to automatically generate engaging clips that have the potential to go viral.","who":"Klap App is designed for content creators, social media influencers, marketers, and anyone looking to create engaging short videos for platforms like TikTok, Reels, and Shorts. It is especially useful for those who want to save time and effort in video editing and reach a wider audience with viral content.","best_features":["AI-powered video editing that extracts the best topics from the video and creates viral short clips","Auto reframing feature that ensures the video always focuses on the most important part (face detection)","Engaging captions generated dynamically to keep viewers engaged"],"pricing":[{"plan":"Klap Pro","price":29,"billing_cycle":"month","original_price":0,"discount":"1 free trial video","features":["AI-powered video editing","Auto reframing","Engaging captions"]}]}', 'Klap App is an AI-powered tool that simplifies the process of creating short videos for platforms like TikTok, Reels, and Shorts. It offers features such as best topic detection, multi-lingual auto captions, and a focus on faces to enhance the video creation experience. By automatically generating engaging clips, Klap saves users valuable time and effort, allowing them to reach a wider audience with viral content.', NULL, '["AI-Powered Tools","Social Media"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('38471ff4-c84e-4ae2-9785-b7375d758be4', '2024-04-12T15:29:21.343Z', 'Klap', 'https://klap.app/', 'images/klap.webp', 'images/klap-icon.webp', '{"what":"Klap is an AI-powered tool that enables users to create short-form videos for various social media platforms like TikTok, Shorts, and Reels from their existing YouTube videos with just one click.","who":"Klap is designed for content creators who want to expand their reach on different social media platforms without investing extra time and effort.","best_features":["AI-powered topic detection for generating engaging short clips","Multi-lingual auto-captioning feature for wider audience reach","Customization options to tailor videos to fit the user''s brand"]}', 'Klap is an AI tool endorsed by major social media platforms that helps content creators effortlessly convert their YouTube videos into ready-to-publish short videos for TikTok, Shorts, and Reels. With features like topic detection, auto-captioning, and customization options, Klap streamlines the process of reaching new audiences and expanding content reach.', NULL, '["Social Media","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8f9271b3-1718-4318-8a07-9ebe112b39d9', '2024-04-12T15:29:21.344Z', 'Klart AI', 'https://www.chatgptservices.app/', 'images/klart-ai.webp', 'images/klart-ai-icon.webp', '{"what":"Klart AI is an AI tool called ChatGPT AI Mail Assistant, designed as a Gmail add-on to enhance email communication using OpenAI''s GPT-4 model. It offers features like generating quick email responses, summarizing emails, translating messages, improving grammar, and providing real-time support through ChatGPT within Gmail.","who":"Klart AI is ideal for professionals, businesses, and individuals who want to streamline their email communication process, improve response times, enhance language capabilities, and receive real-time support within Gmail. It caters to those looking to boost productivity and efficiency in managing their email correspondence.","best_features":["Quick and accurate email response generation","Real-time support through ChatGPT within Gmail","Built-in translation in 13 languages"],"pricing":[]}', 'Klart AI, the ChatGPT AI Mail Assistant, is a Gmail add-on powered by OpenAI''s GPT-4 model. It offers advanced language capabilities to streamline email communication by generating quick responses, summarizing emails, translating messages, improving grammar, and providing real-time support within Gmail. With features to enhance professionalism, detect emotions, and bridge language gaps, Klart AI is a valuable tool for improving email efficiency and productivity.', NULL, '["Email Management","AI Assistance","Translation"]');
