INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9c904201-02b0-457f-9308-8ac40f3fb275', '2024-04-12T15:29:21.344Z', 'Klenty', 'https://www.klenty.com/feature/ai-cadences', 'images/klenty.webp', 'images/klenty-icon.webp', '{"what":"Klenty is a sales engagement platform that offers AI-powered tools to streamline and enhance the sales process for sales teams.","who":"This product is designed for sales teams looking to increase their efficiency and effectiveness in creating sales sequences and engaging with prospects.","best_features":["Multi-Mode Sales Dialer for engaging with prospects through Emails, Calls, LinkedIn, and Texts","Behavior-Triggered Playbooks for executing intelligent outreach based on prospect intent","AI Writer for generating personalized emails, call scripts, and LinkedIn messages in seconds"],"pricing":[]}', 'AI Cadences by Klenty is a sales engagement platform that offers AI-powered tools to assist sales teams in creating sales sequences quickly and efficiently. It enables users to engage with prospects through various channels like Emails, Calls, LinkedIn, and Texts. The platform also provides features like Behavior-Triggered Playbooks and an AI Writer to optimize outreach strategies and personalize communication with prospects.', NULL, '["AI Tools","Sales Engagement","AI Solutions"]');
