INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3eb9fe79-93a1-4cf9-a72e-460b8df132fc', '2024-04-12T15:29:21.363Z', 'LangCSS', 'https://langcss.com/', 'images/langcss.webp', 'images/langcss-icon.svg', '{"what":"LangCSS is an AI assistant designed to help users create designs using Tailwind CSS framework. It allows users to have conversations with the AI to generate forms, buttons, landing pages, and more.","who":"LangCSS is for designers, developers, and anyone looking to create visually appealing designs with the help of AI. It is suitable for individuals and businesses seeking to streamline their design process.","best_features":["Real-time visualization of designs","Incorporation of efficient free components from popular interfaces","Support for SVG and HTML editing during conversations"],"pricing":[]}', 'LangCSS is an AI assistant that assists users in creating designs using the Tailwind CSS framework. Users can interact with the AI to generate various design elements such as forms, buttons, and landing pages, with real-time visualization. The tool incorporates free components from popular interfaces, supports SVG, and allows HTML edits during conversations. Users retain ownership of the code generated, and the tool includes a code editor for easy editing. LangCSS is currently in its early beta phase, continuously improving its features and performance.', NULL, '["AI Tools","CSS Framework","Art Generation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bb2eeb3d-7b9e-4a6e-a4a6-9d1dffab06bd', '2024-04-12T15:29:21.363Z', 'Langdock', 'https://www.langdock.com/', 'images/langdock.webp', 'images/langdock-icon.webp', '{"what":"Langdock is an all-in-one product suite for creating, testing, deploying, and monitoring LLM (Language Model Middleware) plugins, enabling users to easily integrate AI models into their own applications.","who":"Langdock is designed for developers, businesses, and enterprises looking to streamline the integration of AI models into their applications, with a focus on security, compliance, and customization.","best_features":["Detailed request logging and real-time monitoring for tracking plugin usage","Flexible solution for integrating AI models with support for various providers","Extensive documentation, tutorials, and FAQs for user assistance"]}', 'Langdock is an all-in-one platform for creating, testing, deploying, and monitoring LLM plugins to integrate AI models into applications. It offers detailed request logging, real-time monitoring, and support for various AI providers. Langdock provides security features, compliance, and customization options for enterprise users. The platform is available for free with usage limits or paid per use for unrestricted access. Langdock plans to release the platform as open source in June 2023, providing users with the ability to inspect and run the core functionality themselves.', NULL, '["Developer Tools","Enterprise Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('75c8d332-f92f-4441-a761-904545b24462', '2024-04-12T15:29:21.363Z', 'Langmob', 'https://langmob.com/', 'images/langmob.webp', 'images/langmob-icon.webp', '{"what":"LangMob is an AI chatbot language learning app that aims to make language learning accessible, engaging, and personalized for learners worldwide. The app offers interactive conversations, personalized feedback, targeted lessons, and instant assistance with language homework.","who":"LangMob is designed for language learners of all levels who want to improve their language skills in a personalized and engaging way. It is suitable for beginners to advanced learners who are looking for a comprehensive language learning tool.","best_features":["Interactive Conversations: Engage in realistic conversations with the AI chatbot to practice and improve language skills.","Personalized Feedback: Receive feedback tailored to your proficiency level for more effective language practice.","Targeted Lessons: Access specialized lessons in areas such as business vocabulary, medical terminology, and travel phrases to deepen knowledge."],"pricing":""}', 'LangMob is an AI language learning app that offers personalized lessons, interactive conversations, and comprehensive resources for learners of all levels. Its mission is to unlock learners'' linguistic potential and help them confidently communicate in multiple languages.', NULL, '["Language Learning","AI Tools"]');
