INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('56948747-234a-48bf-b6e9-fcb5db76f082', '2024-04-12T15:29:21.387Z', 'Line 21', 'https://line-21.com/', 'images/line-21.webp', 'images/line-21-icon.svg', '{"what":"Line 21 is an AI-supported software designed to facilitate the creation, enhancement, translation, and delivery of live captions. It combines human captioners with AI proofreading to ensure high-quality live content for all users.","who":"Line 21 is for content creators, event organizers, broadcasters, and anyone who requires live captioning services for their audience. It caters to a wide range of industries and languages to promote inclusivity.","best_features":["AI Proofreader for real-time error detection and correction","Automated translation into over 100 languages using custom AI engines","Low latency delivery to various destinations through multiple integration options"]}', 'Line 21 is an innovative AI-supported software that revolutionizes live captioning by combining human expertise with AI capabilities. It offers real-time error detection, automated translation into 100+ languages, and low latency delivery to multiple destinations. Whether for events, broadcasts, or online content, Line 21 ensures accurate and inclusive live captions for all users.', NULL, '["Content Creation","Translation","Accessibility"]');
