INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ff1d3911-bd32-4282-9004-88433f894d85', '2024-04-12T15:29:21.394Z', 'Llama2 by Meta', 'https://ai.meta.com/llama/', 'images/llama2-by-meta.webp', 'images/llama2-by-meta-icon', '{"what":"Llama 2 by Meta is an open-source large language model designed for both research and commercial use. It includes pretrained and fine-tuned language models with parameters ranging from 7B to 70B, outperforming other models in various benchmarks.","who":"Llama 2 is for developers, researchers, companies, and individuals looking to leverage advanced language models for tasks such as reasoning, coding, proficiency tests, and knowledge assessments.","best_features":["Double the context length compared to Llama 1 for enhanced performance","Trained on 2 trillion tokens and over 1 million human annotations for accuracy","Responsible and collaborative AI innovation ecosystem with resources for developers"],"pricing":[]}', 'Llama 2 by Meta is an advanced open-source large language model that offers pretrained and fine-tuned models with superior performance in various benchmarks. It leverages extensive training data and human annotations to provide accurate and reliable results. Meta''s commitment to responsible AI innovation is reflected in the resources and partnerships they offer to support developers and researchers.', NULL, '["Language Models","Open Source","Innovation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('13686c38-6903-4a4f-a14f-a23f1b604f87', '2024-04-12T15:29:21.394Z', 'LLaMa2 Chat', 'https://llama2.ai/', 'images/llama2-chat.webp', 'images/llama2-chat-icon.webp', '{"what":"LLaMa2 Chat is an open-source conversational AI chatbot based on the advanced LLaMA2 model, offering customizable user settings for a personalized chat experience.","who":"LLaMa2 Chat is designed for developers, businesses, and individuals looking for an innovative and customizable conversational AI solution.","best_features":["Flexible user settings for adjustable temperature, top-P, and maximum sequence length for granular control over bot responses.","Advanced AI technology that mirrors human-like conversations with precision.","Open-source nature fostering collaboration and continuous evolution in conversational AI."],"pricing":""}', 'LLaMa2 Chat is an innovative alternative to ChatGPT, offering a paradigm shift in interactive AI with its advanced LLaMA2 model. This chatbot provides a user-centric customization experience with flexible settings for personalized interactions. With its robust learning framework and unique features, LLaMa2 Chat revolutionizes the way we interact with conversational AI, providing a more immersive and tailored chat experience. The open-source nature of LLaMa2 encourages global developer collaboration for ongoing improvements in the field of conversational AI.', NULL, '["Chat Solutions","AI Agents","Open-Source"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e48649b9-6da3-4c7a-96af-429f4836f366', '2024-04-12T15:29:21.394Z', 'Llamaindex', 'https://www.llamaindex.ai/', 'images/llamaindex.webp', 'images/llamaindex-icon.webp', '{"what":"LlamaIndex is a data framework designed for connecting custom data sources to large language models (LLMs), enabling users to integrate various types of data with LLM applications.","who":"LlamaIndex is for enterprises looking to leverage their data for building production-ready LLM applications, as well as developers interested in connecting data sources with LLM frameworks.","best_features":["Seamless integration with 160+ data sources and formats, including APIs, PDFs, documents, and SQL.","Data ingestion capabilities for storing and indexing data for different use cases.","Query interface for knowledge-augmented responses based on user prompts, enabling the creation of powerful end-user applications."],"pricing":[]}', 'LlamaIndex is a data framework that allows users to connect custom data sources to large language models (LLMs) for building powerful applications. It offers seamless integration with various data formats, data ingestion capabilities, and a query interface for knowledge-augmented responses. Users can store, index, and query data for different use cases, enabling the development of automated decision machines, document Q&A applications, and data augmented chatbots.', NULL, '["AI Solutions","Developer Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('34df0d0f-7a5a-405a-8053-0a733a5cb417', '2024-04-12T15:29:21.395Z', 'LLMonitor', 'https://llmonitor.com/', 'images/llmonitor.webp', 'images/llmonitor-icon.webp', '{"what":"LLMonitor is a comprehensive observability and logging platform designed specifically for AI agents and chatbots built on the LLM framework. It offers analytics, tracing, debugging, user activity tracking, and dataset creation features to optimize AI applications.","who":"LLMonitor is for AI developers and companies looking to enhance the performance, behavior, and user interactions of their AI agents and chatbots built on the LLM framework.","best_features":["Analytics and tracing capabilities for monitoring requests and evaluating costs","Replay and debug agent executions to identify issues and improve interactions","User activity tracking and cost visibility for understanding user behavior patterns"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"100% off","features":["Up to 1k events per day","Logs, Metrics, Prompts, Evaluations","Traces, Error stack traces, Live tail"]}]}', 'LLMonitor is a platform tailored for AI agents and chatbots on the LLM framework, providing observability and analytics capabilities to optimize AI applications. It offers features like analytics, tracing, debugging, user activity tracking, and dataset creation to enhance developer experience and user interactions.', NULL, '["Observability","Logging","AI Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('83d27313-1285-4010-9c52-b64f2897201b', '2024-04-12T15:29:21.395Z', 'LLMStack', 'https://llmstack.ai/', 'images/llmstack.webp', 'images/llmstack-icon.webp', '{"what":"LLMStack is a no-code platform that enables users to build generative AI apps, chatbots, and agents using their own data.","who":"LLMStack is for individuals and businesses looking to create AI-powered applications and chatbots without the need for coding skills.","best_features":["Seamless integration with major model providers like OpenAI, Cohere, Stability AI, and Hugging Face.","Ability to import various data sources including web URLs, sitemaps, PDFs, audio files, and more.","Collaborative features for sharing apps publicly or restricting access, with granular permission settings."],"pricing":[]}', 'LLMStack is a no-code platform that empowers users to quickly and easily create AI-powered apps and chatbots using their own data sources and major model providers. The platform supports seamless integration with OpenAI, Cohere, Stability AI, and Hugging Face models, allowing users to supercharge their applications. With the ability to import various data sources and collaborative features for sharing apps, LLMStack provides a comprehensive solution for building generative AI applications. Users can leverage the platform''s responsive user interface, documentation, and community support to enhance their app-building experience.', NULL, '["No-code Development","Collaborative Platforms"]');
