INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('34d3e787-935b-41d2-b2da-c5582be988ed', '2024-04-12T15:29:21.398Z', 'LocGuessr', 'https://locguessr.cyclic.app/', 'images/locguessr.webp', 'images/locguessr-icon.webp', '{"what":"LocGuessr is an AI-based tool focused on location detection using the GPT-4 Vision deep learning model. It analyzes images to determine the geographical location of a specific place.","who":"LocGuessr is designed for developers, researchers, and individuals interested in image recognition, AI, and computer vision technology. It can be used for fun games, hobby projects, or serious research inquiries.","best_features":["Utilizes GPT-4 Vision for accurate location detection","Easy-to-use interface for submitting images","Open-source code available on GitHub for customization and further development"],"pricing":[]}', 'LocGuessr is an AI tool that uses image searches to determine the geographical location of a specific place in an image. It leverages the GPT-4 Vision deep learning model to analyze image features and correlate them with known locations. The platform is open-source, allowing for customization and development by users and developers. LocGuessr enhances image recognition systems with geographical correspondence, pushing the boundaries of AI, machine learning, and computer vision technology.', NULL, '["Image Recognition","Geolocation","Open-Source"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8584c09e-e70c-42d5-9a22-8994e815359a', '2024-04-12T15:29:21.398Z', 'Locket', 'https://www.lovelocket.ai/', 'images/locket.webp', 'images/locket-icon.webp', '{"what":"Locket is an AI-powered application designed to analyze the compatibility between two Instagram profiles. It leverages AI techniques like data scraping, sentiment analysis, and pattern recognition to quantify affinity based on shared interests, interaction dynamics, and content alignment within the Instagram platform.","who":"Locket is for individuals who are curious about the compatibility between their Instagram profiles or those looking to analyze commonalities/similarities between any two Instagram accounts. It provides a unique and entertaining experience for users interested in social media behavior dynamics.","best_features":["AI-powered analysis of Instagram profile compatibility","Quantifies affinity based on shared interests and interaction dynamics","Flexible tool for social media analysis beyond just ''love'' compatibility"]}', 'Locket is an AI-powered application that analyzes the compatibility between two Instagram profiles. It uses AI techniques like data scraping, sentiment analysis, and pattern recognition to quantify affinity based on shared interests, interaction dynamics, and content alignment within the Instagram platform. The tool provides insights into social media behavior dynamics and can be used for both personal and professional purposes.', NULL, '["AI Tools","Entertainment"]');
